INSERT INTO `question_bank` VALUES ('542', '消防车和消火栓的颜色是：', '白色 黄色 红色', 'c', '1', '9', null);
INSERT INTO `question_bank` VALUES ('543', '在火灾逃生方法中，以下不正确的是：', '用湿毛巾捂着嘴巴和鼻子 弯着身子快速跑到安全地点 躲在床底下，等待消防人员救援 马上从最近的消防通道跑到安全地点', 'c', '1', '9', null);
INSERT INTO `question_bank` VALUES ('544', '火灾发生时，湿毛巾折叠8层为宜，其烟雾浓度消除率可达：', '40% 60% 80% 95%', 'b', '1', '9', null);
INSERT INTO `question_bank` VALUES ('545', '压力表与压力容器的接管上装设的三通旋塞阀应设在（ ）管道上', '水平 垂直 任意', 'b', '1', '9', null);
INSERT INTO `question_bank` VALUES ('546', '因实验需要拉接电源线,下列哪种说法是正确的？', ' 不得任意放置于通道上，以免因绝缘破损造成短路或影响通行  插座不足时，可连续串接  插座不足时，可连续分接  不考虑负荷容量', 'a', '1', '9', null);
INSERT INTO `question_bank` VALUES ('547', '停电检修时，在一经合闸即可送电到工作地点的开关或刀闸的操作把手上，应悬挂如下哪种标示牌? ', ' \"\"在此工作”  “止步，高压危险”  “禁止合闸，有人工作”  “今日休息”', 'c', '1', '9', null);
INSERT INTO `question_bank` VALUES ('548', '在微生物实验中，一些不要的菌种等可：', '直接向下水管道倾倒 不需要经消毒和高压灭菌处理后，直接弃掉 经消毒和高压灭菌处理后，再弃掉', 'c', '1', '9', null);
INSERT INTO `question_bank` VALUES ('549', '恒温培养箱的使用最高温度为：', '60℃  100℃   45℃  80℃', 'a', '1', '1', null);
INSERT INTO `question_bank` VALUES ('550', '实验中用到很多玻璃器皿，容易破碎，为避免造成割伤应该注意什么？', ' 装配时不可用力过猛，用力处不可远离连接部位   不能口径不合而勉强连接；   玻璃折断面需烧圆滑，不能有棱角  以上都是', 'd', '1', '1', null);
INSERT INTO `question_bank` VALUES ('551', '过氧化酸、硝酸铵、硝酸钾、高氯酸及其盐、重铬酸及其盐、高锰酸及其盐、过氧化苯甲酸、五氧化二磷等是强氧化剂，使用时应注意：', ' 环境温度不要高于30℃   通风要良好   不要加热，不要与有机物或还原性物质共同使用  以上都是', 'd', '1', '1', null);
INSERT INTO `question_bank` VALUES ('552', '实验大楼安全出口的疏散门应： ', ' 自由开启  向外开启  向内开启  关闭，需要时可自行开启', 'b', '1', '1', null);
