INSERT INTO `question_bank` VALUES ('554', '使用离心机时下列哪种操作是错误的？', ' 离心机必须盖紧盖子  不需要考虑离心管的对角平衡  液体不能超过离心管2/3  每次使用后要清洁离心机腔', 'b', '1', '1', null);
INSERT INTO `question_bank` VALUES ('555', '实验室生物安全防护的内容包括：', '安全设备、个体防护装置和措施  严格的管理制度和标准化的操作程序和规程  实验室的特殊设计和建设要求  以上都是', 'd', '1', '9', null);
INSERT INTO `question_bank` VALUES ('556', '在实验室区域内，可以：', '吸烟、烹饪、用膳 睡觉过夜和进行娱乐活动 做与学习、工作有关的事情', 'c', '1', '9', null);
INSERT INTO `question_bank` VALUES ('557', '开展动物实验过程中，以下哪些做法是正确的？ ', ' 废弃的动物尸体与器官可作为饲料原料再利用  废弃的动物尸体与器官可自行焚烧处理  实验动物排泄物可以作为肥料再利用  实验动物应从国家指定的具有资质的单位获得', 'd', '1', '9', null);
INSERT INTO `question_bank` VALUES ('558', '用过的废洗液应如何处理？', ' 可直接倒入下水道   作为废液交相关部门统一处理   可以用来洗厕所  随意处置', 'b', '1', '9', null);
INSERT INTO `question_bank` VALUES ('559', '化学品的毒性可以通过皮肤吸收、消化道吸收及呼吸道吸收等三种方式对人体健康产生危害，下列不正确的预防措施是：', ' 实验过程中使用三氯甲烷时戴防尘口罩  实验过程中移取强酸、强碱溶液应带防酸碱手套  实验场所严禁携带食物；禁止用饮料瓶装化学药品，防止误食 称取粉末状的有毒药品时，要带口罩防止吸入', 'a', '1', '9', null);
