INSERT INTO `question_bank` VALUES ('609', '化学品的毒性可以通过皮肤吸收、消化道吸收及呼吸道吸收等三种方式对人体健康产生危害，下列不正确的预防措施是：', ' 实验过程中使用三氯甲烷时戴防尘口罩  实验过程中移取强酸、强碱溶液应带防酸碱手套  实验场所严禁携带食物；禁止用饮料瓶装化学药品，防止误食 称取粉末状的有毒药品时，要带口罩防止吸入', 'a', '1', '8', null);
INSERT INTO `question_bank` VALUES ('610', '湿热灭菌是利用热的作用来杀菌，通常在什么设备中进行？ ', ' 高压蒸汽灭菌器  烘箱  高温水浴锅  微波炉', 'a', '1', '8', null);
INSERT INTO `question_bank` VALUES ('611', '被微生物等生物材料污染的金属器皿不可以采用以下哪种溶液进行消毒?', '2%的戊二醛  75%酒精溶液  1%的漂白粉溶液  高浓度肥皂水', 'c', '1', '9', null);
INSERT INTO `question_bank` VALUES ('612', '以下几种气体中，最毒的气体为：', ' 氯气   光气（COCL2）  二氧化硫   三氧化硫', 'b', '1', '9', null);
INSERT INTO `question_bank` VALUES ('613', '下列物质应避免与水接触以免发生危险的是：', ' 氯化钠   氯化钙   四氢化铝   硫酸钙', 'c', '1', '9', null);
INSERT INTO `question_bank` VALUES ('614', '下列何者是会发生爆炸的物质？', ' 氧化锌   三硝基甲苯  四氯化碳   氧化铁', 'b', '1', '9', null);
INSERT INTO `question_bank` VALUES ('615', '遇水发生剧烈反应,容易产生爆炸或燃烧的化学品是:', ' K、NMg、CLi、AlH3、电石   K、NCLi、AlH3、MgO、电石   K、NCLi、AlH3、电石   K、NMg、Li、AlH3、电石', 'c', '1', '9', null);
INSERT INTO `question_bank` VALUES ('616', '眼睛被化学品灼伤后，首先采取的正确方法是：', '点眼药膏  立即开大眼睑，用清水冲洗眼睛  马上到医院看急诊', 'b', '1', '9', null);
INSERT INTO `question_bank` VALUES ('617', '试剂或异物溅入眼内，处理措施正确的是：', ' 溴：大量水洗，再用1％NaHCO3溶液洗  酸：大量水洗，用1～2％NaHCO3溶液洗  碱：大量水洗，再以1％硼酸溶液洗   以上都对', 'b', '1', '9', null);
