INSERT INTO `question_bank` VALUES ('796', '因实验需要拉接电源线,下列哪种说法是正确的？', ' 不得任意放置于通道上，以免因绝缘破损造成短路或影响通行  插座不足时，可连续串接  插座不足时，可连续分接  不考虑负荷容量', 'a c', '2', '6', null);
INSERT INTO `question_bank` VALUES ('797', '停电检修时，在一经合闸即可送电到工作地点的开关或刀闸的操作把手上，应悬挂如下哪种标示牌? ', ' \"\"在此工作”  “止步，高压危险”  “禁止合闸，有人工作”  “今日休息”', 'd c', '2', '6', null);
INSERT INTO `question_bank` VALUES ('798', '在微生物实验中，一些不要的菌种等可：', '直接向下水管道倾倒 不需要经消毒和高压灭菌处理后，直接弃掉 经消毒和高压灭菌处理后，再弃掉', 'd c', '2', '6', null);
INSERT INTO `question_bank` VALUES ('799', '恒温培养箱的使用最高温度为：', '60℃  100℃   45℃  80℃', 'a c', '2', '7', null);
INSERT INTO `question_bank` VALUES ('800', '实验中用到很多玻璃器皿，容易破碎，为避免造成割伤应该注意什么？', ' 装配时不可用力过猛，用力处不可远离连接部位   不能口径不合而勉强连接；   玻璃折断面需烧圆滑，不能有棱角  以上都是', 'd c', '2', '7', null);
INSERT INTO `question_bank` VALUES ('801', '扑救易燃液+C21+B15:G21+B1+B15:F21', ' 用灭火器  用水泼  扑打  以上都可以', 'a c', '2', '7', null);
INSERT INTO `question_bank` VALUES ('802', '使用离心机时下列哪种操作是错误的？', ' 离心机必须盖紧盖子  不需要考虑离心管的对角平衡  液体不能超过离心管2/3  每次使用后要清洁离心机腔', 'b c', '2', '7', null);
INSERT INTO `question_bank` VALUES ('803', '实验室生物安全防护的内容包括：', '安全设备、个体防护装置和措施  严格的管理制度和标准化的操作程序和规程  实验室的特殊设计和建设要求  以上都是', 'd c', '2', '7', null);
INSERT INTO `question_bank` VALUES ('804', '在实验室区域内，可以：', '吸烟、烹饪、用膳 睡觉过夜和进行娱乐活动 做与学习、工作有关的事情', 'd c', '2', '7', null);
INSERT INTO `question_bank` VALUES ('805', '开展动物实验过程中，以下哪些做法是正确的？ ', ' 废弃的动物尸体与器官可作为饲料原料再利用  废弃的动物尸体与器官可自行焚烧处理  实验动物排泄物可以作为肥料再利用  实验动物应从国家指定的具有资质的单位获得', 'd c', '2', '7', null);
INSERT INTO `question_bank` VALUES ('806', '用过的废洗液应如何处理？', ' 可直接倒入下水道   作为废液交相关部门统一处理   可以用来洗厕所  随意处置', 'b c', '2', '8', null);
INSERT INTO `question_bank` VALUES ('807', '化学品的毒性可以通过皮肤吸收、消化道吸收及呼吸道吸收等三种方式对人体健康产生危害，下列不正确的预防措施是：', ' 实验过程中使用三氯甲烷时戴防尘口罩  实验过程中移取强酸、强碱溶液应带防酸碱手套  实验场所严禁携带食物；禁止用饮料瓶装化学药品，防止误食 称取粉末状的有毒药品时，要带口罩防止吸入', 'a c', '2', '8', null);
INSERT INTO `question_bank` VALUES ('808', '湿热灭菌是利用热的作用来杀菌，通常在什么设备中进行？ ', ' 高压蒸汽灭菌器  烘箱  高温水浴锅  微波炉', 'a c', '2', '8', null);
INSERT INTO `question_bank` VALUES ('809', '被微生物等生物材料污染的金属器皿不可以采用以下哪种溶液进行消毒?', '2%的戊二醛  75%酒精溶液  1%的漂白粉溶液  高浓度肥皂水', 'd c', '2', '9', null);
INSERT INTO `question_bank` VALUES ('810', '以下几种气体中，最毒的气体为：', ' 氯气   光气（COCL2）  二氧化硫   三氧化硫', 'b c', '2', '9', null);
INSERT INTO `question_bank` VALUES ('811', '下列物质应避免与水接触以免发生危险的是：', ' 氯化钠   氯化钙   四氢化铝   硫酸钙', 'd c', '2', '9', null);
INSERT INTO `question_bank` VALUES ('812', '下列何者是会发生爆炸的物质？', ' 氧化锌   三硝基甲苯  四氯化碳   氧化铁', 'b c', '2', '9', null);
INSERT INTO `question_bank` VALUES ('813', '遇水发生剧烈反应,容易产生爆炸或燃烧的化学品是:', ' K、NMg、CLi、AlH3、电石   K、NCLi、AlH3、MgO、电石   K、NCLi、AlH3、电石   K、NMg、Li、AlH3、电石', 'd c', '2', '9', null);
INSERT INTO `question_bank` VALUES ('814', '眼睛被化学品灼伤后，首先采取的正确方法是：', '点眼药膏  立即开大眼睑，用清水冲洗眼睛  马上到医院看急诊', 'b c', '2', '9', null);
INSERT INTO `question_bank` VALUES ('815', '试剂或异物溅入眼内，处理措施正确的是：', ' 溴：大量水洗，再用1％NaHCO3溶液洗  酸：大量水洗，用1～2％NaHCO3溶液洗  碱：大量水洗，再以1％硼酸溶液洗   以上都对', 'b c', '2', '9', null);
INSERT INTO `regulation_rules` VALUES ('1', '中华人民共和国消防法', '2018-10-12', '<p style=\"text-indent:28px\">\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; color: black;\">&nbsp;</span><strong><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; color: black;\">目　录</span></strong></span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">第一章　总　则</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">第二章　火灾预防</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">第三章　消防组织</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">第四章　灭火救援</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">第五章　监督检查</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">第六章　法律责任</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><span style=\"font-size: 16px; font-family: 宋体; color: black;\">第七章　附　则</span><span style=\"font-size: 16px; font-family: Arial, sans-serif; color: black;\">&nbsp;</span></span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><strong><span style=\"font-size: 16px; font-family: 宋体; color: black;\">第一章　总　则</span></strong><strong><span style=\"font-size: 16px; font-family: Arial, sans-serif; color: black;\">&nbsp;</span></strong></span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">第一条　为了预防火灾和减少火灾危害，加强应急救援工作，保护人身、财产安全，维护公共安全，制定本法。</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">第二条　消防工作贯彻预防为主、防消结合的方针，按照政府统一领导、部门依法监管、单位全面负责、公民积极参与的原则，实行消防安全责任制，建立健全社会化的消防工作网络。</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">第三条　国务院领导全国的消防工作。地方各级人民政府负责本行政区域内的消防工作。</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">各级人民政府应当将消防工作纳入国民经济和社会发展计划，保障消防工作与经济社会发展相适应。</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">第四条　国务院公安部门对全国的消防工作实施监督管理。县级以上地方人民政府公安机关对本行政区域内的消防工作实施监督管理，并由本级人民政府公安机关消防机构负责实施。军事设施的消防工作，由其主管单位监督管理，公安机关消防机构协助；矿井地下部分、核电厂、海上石油天然气设施的消防工作，由其主管单位监督管理。</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">县级以上人民政府其他有关部门在各自的职责范围内，依照本法和其他相关法律、法规的规定做好消防工作。</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">法律、行政法规对森林、草原的消防工作另有规定的，从其规定。</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">第五条　任何单位和个人都有维护消防安全、保护消防设施、预防火灾、报告火警的义务。任何单位和成年人都有参加有组织的灭火工作的义务。</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">第六条　各级人民政府应当组织开展经常性的消防宣传教育，提高公民的消防安全意识。</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">机关、团体、企业、事业等单位，应当加强对本单位人员的消防宣传教育。</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">公安机关及其消防机构应当加强消防法律、法规的宣传，并督促、指导、协助有关单位做好消防宣传教育工作。</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">教育、人力资源行政主管部门和学校、有关职业培训机构应当将消防知识纳入教育、教学、培训的内容。</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">新闻、广播、电视等有关单位，应当有针对性地面向社会进行消防宣传教育。</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">工会、共产主义青年团、妇女联合会等团体应当结合各自工作对象的特点，组织开展消防宣传教育。</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">村民委员会、居民委员会应当协助人民政府以及公安机关等部门，加强消防宣传教育。</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">第七条　国家鼓励、支持消防科学研究和技术创新，推广使用先进的消防和应急救援技术、设备；鼓励、支持社会力量开展消防公益活动。</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><span style=\"font-size: 16px; font-family: 宋体; color: black;\">对在消防工作中有突出贡献的单位和个人，应当按照国家有关规定给予表彰和奖励。</span><span style=\"font-size: 16px; font-family: Arial, sans-serif; color: black;\">&nbsp;</span></span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><strong><span style=\"font-size: 16px; font-family: 宋体; color: black;\">第二章　火灾预防</span></strong><strong><span style=\"font-size: 16px; font-family: Arial, sans-serif; color: black;\">&nbsp;</span></strong></span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">第八条　地方各级人民政府应当将包括消防安全布局、消防站、消防供水、消防通信、消防车通道、消防装备等内容的消防规划纳入城乡规划，并负责组织实施。</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">城乡消防安全布局不符合消防安全要求的，应当调整、完善；公共消防设施、消防装备不足或者不适应实际需要的，应当增建、改建、配置或者进行技术改造。</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">第九条　建设工程的消防设计、施工必须符合国家工程建设消防技术标准。建设、设计、施工、工程监理等单位依法对建设工程的消防设计、施工质量负责。</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">第十条　按照国家工程建设消防技术标准需要进行消防设计的建设工程，除本法第十一条另有规定的外，建设单位应当自依法取得施工许可之日起七个工作日内，将消防设计文件报公安机关消防机构备案，公安机关消防机构应当进行抽查。</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">第十一条　国务院公安部门规定的大型的人员密集场所和其他特殊建设工程，建设单位应当将消防设计文件报送公安机关消防机构审核。公安机关消防机构依法对审核的结果负责。</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">第十二条　依法应当经公安机关消防机构进行消防设计审核的建设工程，未经依法审核或者审核不合格的，负责审批该工程施工许可的部门不得给予施工许可，建设单位、施工单位不得施工；其他建设工程取得施工许可后经依法抽查不合格的，应当停止施工。</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">第十三条　按照国家工程建设消防技术标准需要进行消防设计的建设工程竣工，依照下列规定进行消防验收、备案：</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">（一）本法第十一条规定的建设工程，建设单位应当向公安机关消防机构申请消防验收；</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">（二）其他建设工程，建设单位在验收后应当报公安机关消防机构备案，公安机关消防机构应当进行抽查。</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">依法应当进行消防验收的建设工程，未经消防验收或者消防验收不合格的，禁止投入使用；其他建设工程经依法抽查不合格的，应当停止使用。</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">第十四条　建设工程消防设计审核、消防验收、备案和抽查的具体办法，由国务院公安部门规定。</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">第十五条　公众聚集场所在投入使用、营业前，建设单位或者使用单位应当向场所所在地的县级以上地方人民政府公安机关消防机构申请消防安全检查。</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">公安机关消防机构应当自受理申请之日起十个工作日内，根据消防技术标准和管理规定，对该场所进行消防安全检查。未经消防安全检查或者经检查不符合消防安全要求的，不得投入使用、营业。</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">第十六条　机关、团体、企业、事业等单位应当履行下列消防安全职责：</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">（一）落实消防安全责任制，制定本单位的消防安全制度、消防安全操作规程，制定灭火和应急疏散预案；</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">（二）按照国家标准、行业标准配置消防设施、器材，设置消防安全标志，并定期组织检验、维修，确保完好有效；</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">（三）对建筑消防设施每年至少进行一次全面检测，确保完好有效，检测记录应当完整准确，存档备查；</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">（四）保障疏散通道、安全出口、消防车通道畅通，保证防火防烟分区、防火间距符合消防技术标准；</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">（五）组织防火检查，及时消除火灾隐患；</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">（六）组织进行有针对性的消防演练；</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">（七）法律、法规规定的其他消防安全职责。</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">单位的主要负责人是本单位的消防安全责任人。</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">第十七条　县级以上地方人民政府公安机关消防机构应当将发生火灾可能性较大以及发生火灾可能造成重大的人身伤亡或者财产损失的单位，确定为本行政区域内的消防安全重点单位，并由公安机关报本级人民政府备案。</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">消防安全重点单位除应当履行本法第十六条规定的职责外，还应当履行下列消防安全职责：</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">（一）确定消防安全管理人，组织实施本单位的消防安全管理工作；</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">（二）建立消防档案，确定消防安全重点部位，设置防火标志，实行严格管理；</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">（三）实行每日防火巡查，并建立巡查记录；</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">（四）对职工进行岗前消防安全培训，定期组织消防安全培训和消防演练。</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">第十八条　同一建筑物由两个以上单位管理或者使用的，应当明确各方的消防安全责任，并确定责任人对共用的疏散通道、安全出口、建筑消防设施和消防车通道进行统一管理。</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">住宅区的物业服务企业应当对管理区域内的共用消防设施进行维护管理，提供消防安全防范服务。</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">第十九条　生产、储存、经营易燃易爆危险品的场所不得与居住场所设置在同一建筑物内，并应当与居住场所保持安全距离。</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">生产、储存、经营其他物品的场所与居住场所设置在同一建筑物内的，应当符合国家工程建设消防技术标准。</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">第二十条　举办大型群众性活动，承办人应当依法向公安机关申请安全许可，制定灭火和应急疏散预案并组织演练，明确消防安全责任分工，确定消防安全管理人员，保持消防设施和消防器材配置齐全、完好有效，保证疏散通道、安全出口、疏散指示标志、应急照明和消防车通道符合消防技术标准和管理规定。</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">第二十一条　禁止在具有火灾、爆炸危险的场所吸烟、使用明火。因施工等特殊情况需要使用明火作业的，应当按照规定事先办理审批手续，采取相应的消防安全措施；作业人员应当遵守消防安全规定。</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">进行电焊、气焊等具有火灾危险作业的人员和自动消防系统的操作人员，必须持证上岗，并遵守消防安全操作规程。</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">第二十二条　生产、储存、装卸易燃易爆危险品的工厂、仓库和专用车站、码头的设置，应当符合消防技术标准。易燃易爆气体和液体的充装站、供应站、调压站，应当设置在符合消防安全要求的位置，并符合防火防爆要求。</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">已经设置的生产、储存、装卸易燃易爆危险品的工厂、仓库和专用车站、码头，易燃易爆气体和液体的充装站、供应站、调压站，不再符合前款规定的，地方人民政府应当组织、协调有关部门、单位限期解决，消除安全隐患。</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">第二十三条　生产、储存、运输、销售、使用、销毁易燃易爆危险品，必须执行消防技术标准和管理规定。</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">进入生产、储存易燃易爆危险品的场所，必须执行消防安全规定。禁止非法携带易燃易爆危险品进入公共场所或者乘坐公共交通工具。</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">储存可燃物资仓库的管理，必须执行消防技术标准和管理规定。</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">第二十四条　消防产品必须符合国家标准；没有国家标准的，必须符合行业标准。禁止生产、销售或者使用不合格的消防产品以及国家明令淘汰的消防产品。</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">依法实行强制性产品认证的消防产品，由具有法定资质的认证机构按照国家标准、行业标准的强制性要求认证合格后，方可生产、销售、使用。实行强制性产品认证的消防产品目录，由国务院产品质量监督部门会同国务院公安部门制定并公布。</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">新研制的尚未制定国家标准、行业标准的消防产品，应当按照国务院产品质量监督部门会同国务院公安部门规定的办法，经技术鉴定符合消防安全要求的，方可生产、销售、使用。</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">依照本条规定经强制性产品认证合格或者技术鉴定合格的消防产品，国务院公安部门消防机构应当予以公布。</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">第二十五条　产品质量监督部门、工商行政管理部门、公安机关消防机构应当按照各自职责加强对消防产品质量的监督检查。</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">第二十六条　建筑构件、建筑材料和室内装修、装饰材料的防火性能必须符合国家标准；没有国家标准的，必须符合行业标准。</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">人员密集场所室内装修、装饰，应当按照消防技术标准的要求，使用不燃、难燃材料。</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">第二十七条　电器产品、燃气用具的产品标准，应当符合消防安全的要求。</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">电器产品、燃气用具的安装、使用及其线路、管路的设计、敷设、维护保养、检测，必须符合消防技术标准和管理规定。</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">第二十八条　任何单位、个人不得损坏、挪用或者擅自拆除、停用消防设施、器材，不得埋压、圈占、遮挡消火栓或者占用防火间距，不得占用、堵塞、封闭疏散通道、安全出口、消防车通道。人员密集场所的门窗不得设置影响逃生和灭火救援的障碍物。</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">第二十九条　负责公共消防设施维护管理的单位，应当保持消防供水、消防通信、消防车通道等公共消防设施的完好有效。在修建道路以及停电、停水、截断通信线路时有可能影响消防队灭火救援的，有关单位必须事先通知当地公安机关消防机构。</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">第三十条　地方各级人民政府应当加强对农村消防工作的领导，采取措施加强公共消防设施建设，组织建立和督促落实消防安全责任制。</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">第三十一条　在农业收获季节、森林和草原防火期间、重大节假日期间以及火灾多发季节，地方各级人民政府应当组织开展有针对性的消防宣传教育，采取防火措施，进行消防安全检查。</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">第三十二条　乡镇人民政府、城市街道办事处应当指导、支持和帮助村民委员会、居民委员会开展群众性的消防工作。村民委员会、居民委员会应当确定消防安全管理人，组织制定防火安全公约，进行防火安全检查。</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">第三十三条　国家鼓励、引导公众聚集场所和生产、储存、运输、销售易燃易爆危险品的企业投保火灾公众责任保险；鼓励保险公司承保火灾公众责任保险。</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">第三十四条　消防产品质量认证、消防设施检测、消防安全监测等消防技术服务机构和执业人员，应当依法获得相应的资质、资格；依照法律、行政法规、国家标准、行业标准和执业准则，接受委托提供消防技术服务，并对服务质量负责。</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><strong><span style=\"font-size: 16px; font-family: 宋体; color: black;\">第三章　消防组织</span></strong></span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">第三十五条　各级人民政府应当加强消防组织建设，根据经济社会发展的需要，建立多种形式的消防组织，加强消防技术人才培养，增强火灾预防、扑救和应急救援的能力。</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">第三十六条　县级以上地方人民政府应当按照国家规定建立公安消防队、专职消防队，并按照国家标准配备消防装备，承担火灾扑救工作。</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">乡镇人民政府应当根据当地经济发展和消防工作的需要，建立专职消防队、志愿消防队，承担火灾扑救工作。</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">第三十七条　公安消防队、专职消防队按照国家规定承担重大灾害事故和其他以抢救人员生命为主的应急救援工作。</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">第三十八条　公安消防队、专职消防队应当充分发挥火灾扑救和应急救援专业力量的骨干作用；按照国家规定，组织实施专业技能训练，配备并维护保养装备器材，提高火灾扑救和应急救援的能力。</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">第三十九条　下列单位应当建立单位专职消防队，承担本单位的火灾扑救工作：</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">（一）大型核设施单位、大型发电厂、民用机场、主要港口；</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">（二）生产、储存易燃易爆危险品的大型企业；</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">（三）储备可燃的重要物资的大型仓库、基地；</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">（四）第一项、第二项、第三项规定以外的火灾危险性较大、距离公安消防队较远的其他大型企业；</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">（五）距离公安消防队较远、被列为全国重点文物保护单位的古建筑群的管理单位。</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">第四十条　专职消防队的建立，应当符合国家有关规定，并报当地公安机关消防机构验收。</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">专职消防队的队员依法享受社会保险和福利待遇。</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">第四十一条　机关、团体、企业、事业等单位以及村民委员会、居民委员会根据需要，建立志愿消防队等多种形式的消防组织，开展群众性自防自救工作。</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">第四十二条　公安机关消防机构应当对专职消防队、志愿消防队等消防组织进行业务指导；根据扑救火灾的需要，可以调动指挥专职消防队参加火灾扑救工作。</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><strong><span style=\"font-size: 16px; font-family: 宋体; color: black;\">第四章　灭火救援</span></strong></span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">第四十三条　县级以上地方人民政府应当组织有关部门针对本行政区域内的火灾特点制定应急预案，建立应急反应和处置机制，为火灾扑救和应急救援工作提供人员、装备等保障。</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">第四十四条　任何人发现火灾都应当立即报警。任何单位、个人都应当无偿为报警提供便利，不得阻拦报警。严禁谎报火警。</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">人员密集场所发生火灾，该场所的现场工作人员应当立即组织、引导在场人员疏散。</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">任何单位发生火灾，必须立即组织力量扑救。邻近单位应当给予支援。</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">消防队接到火警，必须立即赶赴火灾现场，救助遇险人员，排除险情，扑灭火灾。</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">第四十五条　公安机关消防机构统一组织和指挥火灾现场扑救，应当优先保障遇险人员的生命安全。</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">火灾现场总指挥根据扑救火灾的需要，有权决定下列事项：</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">（一）使用各种水源；</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">（二）截断电力、可燃气体和可燃液体的输送，限制用火用电；</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">（三）划定警戒区，实行局部交通管制；</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">（四）利用临近建筑物和有关设施；</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">（五）为了抢救人员和重要物资，防止火势蔓延，拆除或者破损毗邻火灾现场的建筑物、构筑物或者设施等；</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">（六）调动供水、供电、供气、通信、医疗救护、交通运输、环境保护等有关单位协助灭火救援。</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">根据扑救火灾的紧急需要，有关地方人民政府应当组织人员、调集所需物资支援灭火。</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">第四十六条　公安消防队、专职消防队参加火灾以外的其他重大灾害事故的应急救援工作，由县级以上人民政府统一领导。</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">第四十七条　消防车、消防艇前往执行火灾扑救或者应急救援任务，在确保安全的前提下，不受行驶速度、行驶路线、行驶方向和指挥信号的限制，其他车辆、船舶以及行人应当让行，不得穿插超越；收费公路、桥梁免收车辆通行费。交通管理指挥人员应当保证消防车、消防艇迅速通行。</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">赶赴火灾现场或者应急救援现场的消防人员和调集的消防装备、物资，需要铁路、水路或者航空运输的，有关单位应当优先运输。</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">第四十八条　消防车、消防艇以及消防器材、装备和设施，不得用于与消防和应急救援工作无关的事项。</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">第四十九条　公安消防队、专职消防队扑救火灾、应急救援，不得收取任何费用。</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">单位专职消防队、志愿消防队参加扑救外单位火灾所损耗的燃料、灭火剂和器材、装备等，由火灾发生地的人民政府给予补偿。</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">第五十条　对因参加扑救火灾或者应急救援受伤、致残或者死亡的人员，按照国家有关规定给予医疗、抚恤。</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">第五十一条　公安机关消防机构有权根据需要封闭火灾现场，负责调查火灾原因，统计火灾损失。</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">火灾扑灭后，发生火灾的单位和相关人员应当按照公安机关消防机构的要求保护现场，接受事故调查，如实提供与火灾有关的情况。</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">公安机关消防机构根据火灾现场勘验、调查情况和有关的检验、鉴定意见，及时制作火灾事故认定书，作为处理火灾事故的证据。</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><strong><span style=\"font-size: 16px; font-family: 宋体; color: black;\">第五章　监督检查</span></strong></span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">第五十二条　地方各级人民政府应当落实消防工作责任制，对本级人民政府有关部门履行消防安全职责的情况进行监督检查。</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">县级以上地方人民政府有关部门应当根据本系统的特点，有针对性地开展消防安全检查，及时督促整改火灾隐患。</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">第五十三条　公安机关消防机构应当对机关、团体、企业、事业等单位遵守消防法律、法规的情况依法进行监督检查。公安派出所可以负责日常消防监督检查、开展消防宣传教育，具体办法由国务院公安部门规定。</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">公安机关消防机构、公安派出所的工作人员进行消防监督检查，应当出示证件。</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">第五十四条　公安机关消防机构在消防监督检查中发现火灾隐患的，应当通知有关单位或者个人立即采取措施消除隐患；不及时消除隐患可能严重威胁公共安全的，公安机关消防机构应当依照规定对危险部位或者场所采取临时查封措施。</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">第五十五条　公安机关消防机构在消防监督检查中发现城乡消防安全布局、公共消防设施不符合消防安全要求，或者发现本地区存在影响公共安全的重大火灾隐患的，应当由公安机关书面报告本级人民政府。</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">接到报告的人民政府应当及时核实情况，组织或者责成有关部门、单位采取措施，予以整改。</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">第五十六条　公安机关消防机构及其工作人员应当按照法定的职权和程序进行消防设计审核、消防验收和消防安全检查，做到公正、严格、文明、高效。</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">公安机关消防机构及其工作人员进行消防设计审核、消防验收和消防安全检查等，不得收取费用，不得利用消防设计审核、消防验收和消防安全检查谋取利益。公安机关消防机构及其工作人员不得利用职务为用户、建设单位指定或者变相指定消防产品的品牌、销售单位或者消防技术服务机构、消防设施施工单位。</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">第五十七条　公安机关消防机构及其工作人员执行职务，应当自觉接受社会和公民的监督。</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">任何单位和个人都有权对公安机关消防机构及其工作人员在执法中的违法行为进行检举、控告。收到检举、控告的机关，应当按照职责及时查处。</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><strong><span style=\"font-size: 16px; font-family: 宋体; color: black;\">第六章　法律责任</span></strong></span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">第五十八条　违反本法规定，有下列行为之一的，责令停止施工、停止使用或者停产停业，并处三万元以上三十万元以下罚款：</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">（一）依法应当经公安机关消防机构进行消防设计审核的建设工程，未经依法审核或者审核不合格，擅自施工的；</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">（二）消防设计经公安机关消防机构依法抽查不合格，不停止施工的；</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">（三）依法应当进行消防验收的建设工程，未经消防验收或者消防验收不合格，擅自投入使用的；</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">（四）建设工程投入使用后经公安机关消防机构依法抽查不合格，不停止使用的；</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">（五）公众聚集场所未经消防安全检查或者经检查不符合消防安全要求，擅自投入使用、营业的。</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">建设单位未依照本法规定将消防设计文件报公安机关消防机构备案，或者在竣工后未依照本法规定报公安机关消防机构备案的，责令限期改正，处五千元以下罚款。</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">第五十九条　违反本法规定，有下列行为之一的，责令改正或者停止施工，并处一万元以上十万元以下罚款：</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">（一）建设单位要求建筑设计单位或者建筑施工企业降低消防技术标准设计、施工的；</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">（二）建筑设计单位不按照消防技术标准强制性要求进行消防设计的；</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">（三）建筑施工企业不按照消防设计文件和消防技术标准施工，降低消防施工质量的；</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">（四）工程监理单位与建设单位或者建筑施工企业串通，弄虚作假，降低消防施工质量的。</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">第六十条　单位违反本法规定，有下列行为之一的，责令改正，处五千元以上五万元以下罚款：</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">（一）消防设施、器材或者消防安全标志的配置、设置不符合国家标准、行业标准，或者未保持完好有效的；</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">（二）损坏、挪用或者擅自拆除、停用消防设施、器材的；</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">（三）占用、堵塞、封闭疏散通道、安全出口或者有其他妨碍安全疏散行为的；</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">（四）埋压、圈占、遮挡消火栓或者占用防火间距的；</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">（五）占用、堵塞、封闭消防车通道，妨碍消防车通行的；</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">（六）人员密集场所在门窗上设置影响逃生和灭火救援的障碍物的；</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">（七）对火灾隐患经公安机关消防机构通知后不及时采取措施消除的。</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">个人有前款第二项、第三项、第四项、第五项行为之一的，处警告或者五百元以下罚款。</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">有本条第一款第三项、第四项、第五项、第六项行为，经责令改正拒不改正的，强制执行，所需费用由违法行为人承担。</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">第六十一条　生产、储存、经营易燃易爆危险品的场所与居住场所设置在同一建筑物内，或者未与居住场所保持安全距离的，责令停产停业，并处五千元以上五万元以下罚款。</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">生产、储存、经营其他物品的场所与居住场所设置在同一建筑物内，不符合消防技术标准的，依照前款规定处罚。</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">第六十二条　有下列行为之一的，依照《中华人民共和国治安管理处罚法》的规定处罚：</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">（一）违反有关消防技术标准和管理规定生产、储存、运输、销售、使用、销毁易燃易爆危险品的；</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">（二）非法携带易燃易爆危险品进入公共场所或者乘坐公共交通工具的；</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">（三）谎报火警的；</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">（四）阻碍消防车、消防艇执行任务的；</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">（五）阻碍公安机关消防机构的工作人员依法执行职务的。</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">第六十三条　违反本法规定，有下列行为之一的，处警告或者五百元以下罚款；情节严重的，处五日以下拘留：</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">（一）违反消防安全规定进入生产、储存易燃易爆危险品场所的；</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">（二）违反规定使用明火作业或者在具有火灾、爆炸危险的场所吸烟、使用明火的。</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">第六十四条　违反本法规定，有下列行为之一，尚不构成犯罪的，处十日以上十五日以下拘留，可以并处五百元以下罚款；情节较轻的，处警告或者五百元以下罚款：</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">（一）指使或者强令他人违反消防安全规定，冒险作业的；</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">（二）过失引起火灾的；</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">（三）在火灾发生后阻拦报警，或者负有报告职责的人员不及时报警的；</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">（四）扰乱火灾现场秩序，或者拒不执行火灾现场指挥员指挥，影响灭火救援的；</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">（五）故意破坏或者伪造火灾现场的；</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">（六）擅自拆封或者使用被公安机关消防机构查封的场所、部位的。</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">第六十五条　违反本法规定，生产、销售不合格的消防产品或者国家明令淘汰的消防产品的，由产品质量监督部门或者工商行政管理部门依照《中华人民共和国产品质量法》的规定从重处罚。</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">人员密集场所使用不合格的消防产品或者国家明令淘汰的消防产品的，责令限期改正；逾期不改正的，处五千元以上五万元以下罚款，并对其直接负责的主管人员和其他直接责任人员处五百元以上二千元以下罚款；情节严重的，责令停产停业。</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">公安机关消防机构对于本条第二款规定的情形，除依法对使用者予以处罚外，应当将发现不合格的消防产品和国家明令淘汰的消防产品的情况通报产品质量监督部门、工商行政管理部门。产品质量监督部门、工商行政管理部门应当对生产者、销售者依法及时查处。</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">第六十六条　电器产品、燃气用具的安装、使用及其线路、管路的设计、敷设、维护保养、检测不符合消防技术标准和管理规定的，责令限期改正；逾期不改正的，责令停止使用，可以并处一千元以上五千元以下罚款。</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">第六十七条　机关、团体、企业、事业等单位违反本法第十六条、第十七条、第十八条、第二十一条第二款规定的，责令限期改正；逾期不改正的，对其直接负责的主管人员和其他直接责任人员依法给予处分或者给予警告处罚。</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">第六十八条　人员密集场所发生火灾，该场所的现场工作人员不履行组织、引导在场人员疏散的义务，情节严重，尚不构成犯罪的，处五日以上十日以下拘留。</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">第六十九条　消防产品质量认证、消防设施检测等消防技术服务机构出具虚假文件的，责令改正，处五万元以上十万元以下罚款，并对直接负责的主管人员和其他直接责任人员处一万元以上五万元以下罚款；有违法所得的，并处没收违法所得；给他人造成损失的，依法承担赔偿责任；情节严重的，由原许可机关依法责令停止执业或者吊销相应资质、资格。</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">前款规定的机构出具失实文件，给他人造成损失的，依法承担赔偿责任；造成重大损失的，由原许可机关依法责令停止执业或者吊销相应资质、资格。</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">第七十条　本法规定的行政处罚，除本法另有规定的外，由公安机关消防机构决定；其中拘留处罚由县级以上公安机关依照《中华人民共和国治安管理处罚法》的有关规定决定。</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">公安机关消防机构需要传唤消防安全违法行为人的，依照《中华人民共和国治安管理处罚法》的有关规定执行。</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">被责令停止施工、停止使用、停产停业的，应当在整改后向公安机关消防机构报告，经公安机关消防机构检查合格，方可恢复施工、使用、生产、经营。</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">当事人逾期不执行停产停业、停止使用、停止施工决定的，由作出决定的公安机关消防机构强制执行。</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">责令停产停业，对经济和社会生活影响较大的，由公安机关消防机构提出意见，并由公安机关报请本级人民政府依法决定。本级人民政府组织公安机关等部门实施。</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">第七十一条　公安机关消防机构的工作人员滥用职权、玩忽职守、徇私舞弊，有下列行为之一，尚不构成犯罪的，依法给予处分：</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">（一）对不符合消防安全要求的消防设计文件、建设工程、场所准予审核合格、消防验收合格、消防安全检查合格的；</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">（二）无故拖延消防设计审核、消防验收、消防安全检查，不在法定期限内履行职责的；</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">（三）发现火灾隐患不及时通知有关单位或者个人整改的；</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">（四）利用职务为用户、建设单位指定或者变相指定消防产品的品牌、销售单位或者消防技术服务机构、消防设施施工单位的；</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">（五）将消防车、消防艇以及消防器材、装备和设施用于与消防和应急救援无关的事项的；</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">（六）其他滥用职权、玩忽职守、徇私舞弊的行为。</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">建设、产品质量监督、工商行政管理等其他有关行政主管部门的工作人员在消防工作中滥用职权、玩忽职守、徇私舞弊，尚不构成犯罪的，依法给予处分。</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">第七十二条　违反本法规定，构成犯罪的，依法追究刑事责任。</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><strong><span style=\"font-size: 16px; font-family: 宋体; color: black;\">第七章　附　则</span></strong></span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">第七十三条　本法下列用语的含义：</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">（一）消防设施，是指火灾自动报警系统、自动灭火系统、消火栓系统、防烟排烟系统以及应急广播和应急照明、安全疏散设施等。</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">（二）消防产品，是指专门用于火灾预防、灭火救援和火灾防护、避难、逃生的产品。</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">（三）公众聚集场所，是指宾馆、饭店、商场、集贸市场、客运车站候车室、客运码头候船厅、民用机场航站楼、体育场馆、会堂以及公共娱乐场所等。</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">（四）人员密集场所，是指公众聚集场所，医院的门诊楼、病房楼，学校的教学楼、图书馆、食堂和集体宿舍，养老院，福利院，托儿所，幼儿园，公共图书馆的阅览室，公共展览馆、博物馆的展示厅，劳动密集型企业的生产加工车间和员工集体宿舍，旅游、宗教活动场所等。</span>\r\n</p>\r\n<p style=\"margin-top:5px;margin-right:0;margin-bottom: 5px;margin-left:0;text-indent:32px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">第七十四条　本法自２００９年５月１日起施行。</span>\r\n</p>\r\n<p style=\"text-indent:43px\">\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">&nbsp;</span>\r\n</p>\r\n<p>\r\n    <br/>\r\n</p>');
INSERT INTO `regulation_rules` VALUES ('2', '中华人民共和国危险化学品安全管理条例', '2018-10-11', '<p style=\"text-align:center;line-height:24px\">\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><strong><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; color: black; letter-spacing: 1px;\">第一章&nbsp;总则</span></strong></span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">&nbsp;</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　第一条为了加强对危险化学品的安全管理，保障人民生命、财产安全，保护环境，制定本条例。</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　第二条在中华人民共和国境内生产、经营、储存、运输、使用危险化学品和处置废弃危险化学品，必须遵守本条例和国家有关安全生产的法律、其他行政法规的规定。</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　第三条本条例所称危险化学品，包括爆炸品、压缩气体和液化气体、易燃液体、易燃固体、自燃物品和遇湿易燃物品、氧化剂和有机过氧化物、有毒品和腐蚀品等。</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　危险化学品列入以国家标准公布的《危险货物品名表》（GB12268）；剧毒化学品目录和未列入《危险货物品名表》的其他危险化学品，由国务院经济贸易综合管理部门会同国务院公安、环境保护、卫生、质检、交通部门确定并公布。</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　第四条生产、经营、储存、运输、使用危险化学品和处置废弃危险化学品的单位（以下统称危险化学品单位），其主要负责人必须保证本单位危险化学品的安全管理符合有关法律、法规、规章的规定和国家标准的要求，并对本单位危险化学品的安全负责。</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　危险化学品单位从事生产、经营、储存、运输、使用危险化学品或者处置废弃危险化学品活动的人员，必须接受有关法律、法规、规章和安全知识、专业技术、职业卫生防护和应急救援知识的培训，并经考核合格，方可上岗作业。</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　第五条对危险化学品的生产、经营、储存、运输、使用和对废弃危险化学品处置实施监督管理的有关部门，依照下列规定履行职责：</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　（一）国务院经济贸易综合管理部门和省、自治区、直辖市人民政府经济贸易管理部门，依照本条例的规定，负责危险化学品安全监督管理综合工作，负责危险化学品生产、储存企业设立及其改建、扩建的审查，负责危险化学品包装物、容器（包括用于运输工具的槽罐，下同）专业生产企业的审查和定点，负责危险化学品经营许可证的发放，负责国内危险化学品的登记，负责危险化学品事故应急救援的组织和协调，并负责前述事项的监督检查；设区的市级人民政府和县级人民政府的负责危险化学品安全监督管理综合工作的部门，由各该级人民政府确定，依照本条例的规定履行职责。</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　（二）公安部门负责危险化学品的公共安全管理，负责发放剧毒化学品购买凭证和准购证，负责审查核发剧毒化学品公路运输通行证，对危险化学品道路运输安全实施监督，并负责前述事项的监督检查。</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　（三）质检部门负责发放危险化学品及其包装物、容器的生产许可证，负责对危险化学品包装物、容器的产品质量实施监督，并负责前述事项的监督检查。</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　（四）环境保护部门负责废弃危险化学品处置的监督管理，负责调查重大危险化学品污染事故和生态破坏事件，负责有毒化学品事故现场的应急监测和进口危险化学品的登记，并负责前述事项的监督检查。</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　（五）铁路、民航部门负责危险化学品铁路、航空运输和危险化学品铁路、民航运输单位及其运输工具的安全管理及监督检查。交通部门负责危险化学品公路、水路运输单位及其运输工具的安全管理，对危险化学品水路运输安全实施监督，负责危险化学品公路、水路运输单位、驾驶人员、船员、装卸人员和押运人员的资质认定，并负责前述事项的监督检查。</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　（六）卫生行政部门负责危险化学品的毒性鉴定和危险化学品事故伤亡人员的医疗救护工作。</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　（七）工商行政管理部门依据有关部门的批准、许可文件，核发危险化学品生产、经营、储存、运输单位营业执照，并监督管理危险化学品市场经营活动。</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　（八）邮政部门负责邮寄危险化学品的监督检查。</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　第六条依照本条例对危险化学品单位实施监督管理的有关部门，依法进行监督检查，可以行使下列职权：</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　（一）进入危险化学品作业场所进行现场检查，调取有关资料，向有关人员了解情况，向危险化学品单位提出整改措施和建议；</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　（二）发现危险化学品事故隐患时，责令立即排除或者限期排除；</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　（三）对有根据认为不符合有关法律、法规、规章规定和国家标准要求的设施、设备、器材和运输工具，责令立即停止使用；</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　（四）发现违法行为，当场予以纠正或者责令限期改正。</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　危险化学品单位应当接受有关部门依法实施的监督检查，不得拒绝、阻挠。</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　有关部门派出的工作人员依法进行监督检查时，应当出示证件。</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">&nbsp;</span>\r\n</p>\r\n<p style=\"text-align:center;line-height:24px\">\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><strong><span style=\"font-size: 24px; font-family: 宋体; color: black; letter-spacing: 1px;\">第二章&nbsp;危险化学品的生产、储存和使用</span></strong></span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　第七条国家对危险化学品的生产和储存实行统一规划、合理布局和严格控制，并对危险化学品生产、储存实行审批制度；未经审批，任何单位和个人都不得生产、储存危险化学品。</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　设区的市级人民政府根据当地经济发展的实际需要，在编制总体规划时，应当按照确保安全的原则规划适当区域专门用于危险化学品的生产、储存。</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　第八条危险化学品生产、储存企业，必须具备下列条件：</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　（一）有符合国家标准的生产工艺、设备或者储存方式、设施；</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　（二）工厂、仓库的周边防护距离符合国家标准或者国家有关规定；</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　（三）有符合生产或者储存需要的管理人员和技术人员；</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　（四）有健全的安全管理制度；</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　（五）符合法律、法规规定和国家标准要求的其他条件。</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　第九条设立剧毒化学品生产、储存企业和其他危险化学品生产、储存企业，应当分别向省、自治区、直辖市人民政府经济贸易管理部门和设区的市级人民政府负责危险化学品安全监督管理综合工作的部门提出申请，并提交下列文件：</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　（一）可行性研究报告；</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　（二）原料、中间产品、最终产品或者储存的危险化学品的燃点、自燃点、闪点、爆炸极限、毒性等理化性能指标；</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　（三）包装、储存、运输的技术要求；</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　（四）安全评价报告；</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　（五）事故应急救援措施；</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　（六）符合本条例第八条规定条件的证明文件。</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　省、自治区、直辖市人民政府经济贸易管理部门或者设区的市级人民政府负责危险化学品安全监督管理综合工作的部门收到申请和提交的文件后，应当组织有关专家进行审查，提出审查意见后，报本级人民政府作出批准或者不予批准的决定。依据本级人民政府的决定，予以批准的，由省、自治区、直辖市人民政府经济贸易管理部门或者设区的市级人民政府负责危险化学品安全监督管理综合工作的部门颁发批准书；不予批准的，书面通知申请人。</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　申请人凭批准书向工商行政管理部门办理登记注册手续。</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　第十条除运输工具加油站、加气站外，危险化学品的生产装置和储存数量构成重大危险源的储存设施，与下列场所、区域的距离必须符合国家标准或者国家有关规定：</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　（一）居民区、商业中心、公园等人口密集区域；</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　（二）学校、医院、影剧院、体育场（馆）等公共设施；</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　（三）供水水源、水厂及水源保护区；</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　（四）车站、码头（按照国家规定，经批准，专门从事危险化学品装卸作业的除外）、机场以及公路、铁路、水路交通干线、地铁风亭及出入口；</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　（五）基本农田保护区、畜牧区、渔业水域和种子、种畜、水产苗种生产基地；</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　（六）河流、湖泊、风景名胜区和自然保护区；</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　（七）军事禁区、军事管理区；</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　（八）法律、行政法规规定予以保护的其他区域。</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　已建危险化学品的生产装置和储存数量构成重大危险源的储存设施不符合前款规定的，由所在地设区的市级人民政府负责危险化学品安全监督管理综合工作的部门监督其在规定期限内进行整顿；需要转产、停产、搬迁、关闭的，报本级人民政府批准后实施。</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　本条例所称重大危险源，是指生产、运输、使用、储存危险化学品或者处置废弃危险化学品，且危险化学品的数量等于或者超过临界量的单元（包括场所和设施）。</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　第十一条危险化学品生产、储存企业改建、扩建的，必须依照本条例第九条的规定经审查批准。</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　第十二条依法设立的危险化学品生产企业，必须向国务院质检部门申请领取危险化学品生产许可证；未取得危险化学品生产许可证的，不得开工生产。</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　国务院质检部门应当将颁发危险化学品生产许可证的情况通报国务院经济贸易综合管理部门、环境保护部门和公安部门。</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　第十三条任何单位和个人不得生产、经营、使用国家明令禁止的危险化学品。</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　禁止用剧毒化学品生产灭鼠药以及其他可能进入人民日常生活的化学产品和日用化学品。</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　第十四条生产危险化学品的，应当在危险化学品的包装内附有与危险化学品完全一致的化学品安全技术说明书，并在包装（包括外包装件）上加贴或者拴挂与包装内危险化学品完全一致的化学品安全标签。</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　危险化学品生产企业发现其生产的危险化学品有新的危害特性时，应当立即公告，并及时修订安全技术说明书和安全标签。</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　第十五条使用危险化学品从事生产的单位，其生产条件必须符合国家标准和国家有关规定，并依照国家有关法律、法规的规定取得相应的许可，必须建立、健全危险化学品使用的安全管理规章制度，保证危险化学品的安全使用和管理。</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　第十六条生产、储存、使用危险化学品的，应当根据危险化学品的种类、特性，在车间、库房等作业场所设置相应的监测、通风、防晒、调温、防火、灭火、防爆、泄压、防毒、消毒、中和、防潮、防雷、防静电、防腐、防渗漏、防护围堤或者隔离操作等安全设施、设备，并按照国家标准和国家有关规定进行维护、保养，保证符合安全运行要求。</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　第十七条生产、储存、使用剧毒化学品的单位，应当对本单位的生产、储存装置每年进行一次安全评价；生产、储存、使用其他危险化学品的单位，应当对本单位的生产、储存装置每两年进行一次安全评价。</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　安全评价报告应当对生产、储存装置存在的安全问题提出整改方案。安全评价中发现生产、储存装置存在现实危险的，应当立即停止使用，予以更换或者修复，并采取相应的安全措施。</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　安全评价报告应当报所在地设区的市级人民政府负责危险化学品安全监督管理综合工作的部门备案。</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　第十八条危险化学品的生产、储存、使用单位，应当在生产、储存和使用场所设置通讯、报警装置，并保证在任何情况下处于正常适用状态。</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　第十九条剧毒化学品的生产、储存、使用单位，应当对剧毒化学品的产量、流向、储存量和用途如实记录，并采取必要的保安措施，防止剧毒化学品被盗、丢失或者误售、误用；发现剧毒化学品被盗、丢失或者误售、误用时，必须立即向当地公安部门报告。</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　第二十条危险化学品的包装必须符合国家法律、法规、规章的规定和国家标准的要求。</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　危险化学品包装的材质、型式、规格、方法和单件质量（重量），应当与所包装的危险化学品的性质和用途相适应，便于装卸、运输和储存。</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　第二十一条危险化学品的包装物、容器，必须由省、自治区、直辖市人民政府经济贸易管理部门审查合格的专业生产企业定点生产，并经国务院质检部门认可的专业检测、检验机构检测、检验合格，方可使用。</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　重复使用的危险化学品包装物、容器在使用前，应当进行检查，并作出记录；检查记录应当至少保存2年。</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　质检部门应当对危险化学品的包装物、容器的产品质量进行定期的或者不定期的检查。</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　第二十二条危险化学品必须储存在专用仓库、专用场地或者专用储存室（以下统称专用仓库）内，储存方式、方法与储存数量必须符合国家标准，并由专人管理。</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　危险化学品出入库，必须进行核查登记。库存危险化学品应当定期检查。</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　剧毒化学品以及储存数量构成重大危险源的其他危险化学品必须在专用仓库内单独存放，实行双人收发、双人保管制度。储存单位应当将储存剧毒化学品以及构成重大危险源的其他危险化学品的数量、地点以及管理人员的情况，报当地公安部门和负责危险化学品安全监督管理综合工作的部门备案。</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　第二十三条危险化学品专用仓库，应当符合国家标准对安全、消防的要求，设置明显标志。危险化学品专用仓库的储存设备和安全设施应当定期检测。</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　第二十四条处置废弃危险化学品，依照固体废物污染环境防治法和国家有关规定执行。</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　第二十五条危险化学品的生产、储存、使用单位转产、停产、停业或者解散的，应当采取有效措施，处置危险化学品的生产或者储存设备、库存产品及生产原料，不得留有事故隐患。处置方案应当报所在地设区的市级人民政府负责危险化学品安全监督管理综合工作的部门和同级环境保护部门、公安部门备案。负责危险化学品安全监督管理综合工作的部门应当对处置情况进行监督检查。</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　第二十六条公众上交的危险化学品，由公安部门接收。公安部门接收的危险化学品和其他有关部门收缴的危险化学品，交由环境保护部门认定的专业单位处理。</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">&nbsp;</span>\r\n</p>\r\n<p style=\"text-align:center;line-height:24px\">\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><strong><span style=\"font-size: 24px; font-family: 宋体; color: black; letter-spacing: 1px;\">第三章&nbsp;危险化学品的经营</span></strong></span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　第二十七条国家对危险化学品经营销售实行许可制度。未经许可，任何单位和个人都不得经营销售危险化学品。</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　第二十八条危险化学品经营企业，必须具备下列条件：</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　（一）经营场所和储存设施符合国家标准；</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　（二）主管人员和业务人员经过专业培训，并取得上岗资格；</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　（三）有健全的安全管理制度；</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　（四）符合法律、法规规定和国家标准要求的其他条件。</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　第二十九条经营剧毒化学品和其他危险化学品的，应当分别向省、自治区、直辖市人民政府经济贸易管理部门或者设区的市级人民政府负责危险化学品安全监督管理综合工作的部门提出申请，并附送本条例第二十八条规定条件的相关证明材料。省、自治区、直辖市人民政府经济贸易管理部门或者设区的市级人民政府负责危险化学品安全监督管理综合工作的部门接到申请后，应当依照本条例的规定对申请人提交的证明材料和经营场所进行审查。经审查，符合条件的，颁发危险化学品经营许可证，并将颁发危险化学品经营许可证的情况通报同级公安部门和环境保护部门；不符合条件的，书面通知申请人并说明理由。</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　申请人凭危险化学品经营许可证向工商行政管理部门办理登记注册手续。</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　第三十条经营危险化学品，不得有下列行为：</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　（一）从未取得危险化学品生产许可证或者危险化学品经营许可证的企业采购危险化学品；</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　（二）经营国家明令禁止的危险化学品和用剧毒化学品生产的灭鼠药以及其他可能进入人民日常生活的化学产品和日用化学品；</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　（三）销售没有化学品安全技术说明书和化学品安全标签的危险化学品。</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　第三十一条危险化学品生产企业不得向未取得危险化学品经营许可证的单位或者个人销售危险化学品。</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　第三十二条危险化学品经营企业储存危险化学品，应当遵守本条例第二章的有关规定。危险化学品商店内只能存放民用小包装的危险化学品，其总量不得超过国家规定的限量。</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　第三十三条剧毒化学品经营企业销售剧毒化学品，应当记录购买单位的名称、地址和购买人员的姓名、身份证号码及所购剧毒化学品的品名、数量、用途。记录应当至少保存１年。</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　剧毒化学品经营企业应当每天核对剧毒化学品的销售情况；发现被盗、丢失、误售等情况时，必须立即向当地公安部门报告。</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　第三十四条购买剧毒化学品，应当遵守下列规定：</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　（一）生产、科研、医疗等单位经常使用剧毒化学品的，应当向设区的市级人民政府公安部门申请领取购买凭证，凭购买凭证购买；</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　（二）单位临时需要购买剧毒化学品的，应当凭本单位出具的证明（注明品名、数量、用途）向设区的市级人民政府公安部门申请领取准购证，凭准购证购买；</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　（三）个人不得购买农药、灭鼠药、灭虫药以外的剧毒化学品。</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　剧毒化学品生产企业、经营企业不得向个人或者无购买凭证、准购证的单位销售剧毒化学品。剧毒化学品购买凭证、准购证不得伪造、变造、买卖、出借或者以其他方式转让，不得使用作废的剧毒化学品购买凭证、准购证。</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　剧毒化学品购买凭证和准购证的式样和具体申领办法由国务院公安部门制定。</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">&nbsp;</span>\r\n</p>\r\n<p style=\"text-align:center;line-height:24px\">\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><strong><span style=\"font-size: 24px; font-family: 宋体; color: black; letter-spacing: 1px;\">第四章&nbsp;危险化学品的运输</span></strong></span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　第三十五条国家对危险化学品的运输实行资质认定制度；未经资质认定，不得运输危险化学品。</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　危险化学品运输企业必须具备的条件由国务院交通部门规定。</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　第三十六条用于危险化学品运输工具的槽罐以及其他容器，必须依照本条例第二十一条的规定，由专业生产企业定点生产，并经检测、检验合格，方可使用。</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　质检部门应当对前款规定的专业生产企业定点生产的槽罐以及其他容器的产品质量进行定期的或者不定期的检查。</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　第三十七条危险化学品运输企业，应当对其驾驶员、船员、装卸管理人员、押运人员进行有关安全知识培训；驾驶员、船员、装卸管理人员、押运人员必须掌握危险化学品运输的安全知识，并经所在地设区的市级人民政府交通部门考核合格（船员经海事管理机构考核合格），取得上岗资格证，方可上岗作业。危险化学品的装卸作业必须在装卸管理人员的现场指挥下进行。</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　运输危险化学品的驾驶员、船员、装卸人员和押运人员必须了解所运载的危险化学品的性质、危害特性、包装容器的使用特性和发生意外时的应急措施。运输危险化学品，必须配备必要的应急处理器材和防护用品。</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　第三十八条通过公路运输危险化学品的，托运人只能委托有危险化学品运输资质的运输企业承运。</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　第三十九条通过公路运输剧毒化学品的，托运人应当向目的地的县级人民政府公安部门申请办理剧毒化学品公路运输通行证。</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　办理剧毒化学品公路运输通行证，托运人应当向公安部门提交有关危险化学品的品名、数量、运输始发地和目的地、运输路线、运输单位、驾驶人员、押运人员、经营单位和购买单位资质情况的材料。</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　剧毒化学品公路运输通行证的式样和具体申领办法由国务院公安部门制定。</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　第四十条禁止利用内河以及其他封闭水域等航运渠道运输剧毒化学品以及国务院交通部门规定禁止运输的其他危险化学品。</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　利用内河以及其他封闭水域等航运渠道运输前款规定以外的危险化学品的，只能委托有危险化学品运输资质的水运企业承运，并按照国务院交通部门的规定办理手续，接受有关交通部门（港口部门、海事管理机构，下同）的监督管理。</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　运输危险化学品的船舶及其配载的容器必须按照国家关于船舶检验的规范进行生产，并经海事管理机构认可的船舶检验机构检验合格，方可投入使用。</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　第四十一条托运人托运危险化学品，应当向承运人说明运输的危险化学品的品名、数量、危害、应急措施等情况。</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　运输危险化学品需要添加抑制剂或者稳定剂的，托运人交付托运时应当添加抑制剂或者稳定剂，并告知承运人。</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　托运人不得在托运的普通货物中夹带危险化学品，不得将危险化学品匿报或者谎报为普通货物托运。</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　第四十二条运输、装卸危险化学品，应当依照有关法律、法规、规章的规定和国家标准的要求并按照危险化学品的危险特性，采取必要的安全防护措施。</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　运输危险化学品的槽罐以及其他容器必须封口严密，能够承受正常运输条件下产生的内部压力和外部压力，保证危险化学品在运输中不因温度、湿度或者压力的变化而发生任何渗（洒）漏。</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　第四十三条通过公路运输危险化学品，必须配备押运人员，并随时处于押运人员的监管之下，不得超装、超载，不得进入危险化学品运输车辆禁止通行的区域；确需进入禁止通行区域的，应当事先向当地公安部门报告，由公安部门为其指定行车时间和路线，运输车辆必须遵守公安部门规定的行车时间和路线。</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　危险化学品运输车辆禁止通行区域，由设区的市级人民政府公安部门划定，并设置明显的标志。</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　运输危险化学品途中需要停车住宿或者遇有无法正常运输的情况时，应当向当地公安部门报告。</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　第四十四条剧毒化学品在公路运输途中发生被盗、丢失、流散、泄漏等情况时，承运人及押运人员必须立即向当地公安部门报告，并采取一切可能的警示措施。公安部门接到报告后，应当立即向其他有关部门通报情况；有关部门应当采取必要的安全措施。</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　第四十五条任何单位和个人不得邮寄或者在邮件内夹带危险化学品，不得将危险化学品匿报或者谎报为普通物品邮寄。</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　第四十六条通过铁路、航空运输危险化学品的，按照国务院铁路、民航部门的有关规定执行。</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">&nbsp;</span>\r\n</p>\r\n<p style=\"text-align:center;line-height:24px\">\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><strong><span style=\"font-size: 24px; font-family: 宋体; color: black; letter-spacing: 1px;\">第五章&nbsp;危险化学品的登记与事故应急救援</span></strong></span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　第四十七条国家实行危险化学品登记制度，并为危险化学品安全管理、事故预防和应急救援提供技术、信息支持。</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　第四十八条危险化学品生产、储存企业以及使用剧毒化学品和数量构成重大危险源的其他危险化学品的单位，应当向国务院经济贸易综合管理部门负责危险化学品登记的机构办理危险化学品登记。危险化学品登记的具体办法由国务院经济贸易综合管理部门制定。</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　负责危险化学品登记的机构应当向环境保护、公安、质检、卫生等有关部门提供危险化学品登记的资料。</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　第四十九条县级以上地方各级人民政府负责危险化学品安全监督管理综合工作的部门应当会同同级其他有关部门制定危险化学品事故应急救援预案，报经本级人民政府批准后实施。</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　第五十条危险化学品单位应当制定本单位事故应急救援预案，配备应急救援人员和必要的应急救援器材、设备，并定期组织演练。</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　危险化学品事故应急救援预案应当报设区的市级人民政府负责危险化学品安全监督管理综合工作的部门备案。</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　第五十一条发生危险化学品事故，单位主要负责人应当按照本单位制定的应急救援预案，立即组织救援，并立即报告当地负责危险化学品安全监督管理综合工作的部门和公安、环境保护、质检部门。</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　第五十二条发生危险化学品事故，有关地方人民政府应当做好指挥、领导工作。负责危险化学品安全监督管理综合工作的部门和环境保护、公安、卫生等有关部门，应当按照当地应急救援预案组织实施救援，不得拖延、推诿。有关地方人民政府及其有关部门并应当按照下列规定，采取必要措施，减少事故损失，防止事故蔓延、扩大：</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　（一）立即组织营救受害人员，组织撤离或者采取其他措施保护危害区域内的其他人员；</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　（二）迅速控制危害源，并对危险化学品造成的危害进行检验、监测，测定事故的危害区域、危险化学品性质及危害程度；</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　（三）针对事故对人体、动植物、土壤、水源、空气造成的现实危害和可能产生的危害，迅速采取封闭、隔离、洗消等措施；</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　（四）对危险化学品事故造成的危害进行监测、处置，直至符合国家环境保护标准。</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　第五十三条危险化学品生产企业必须为危险化学品事故应急救援提供技术指导和必要的协助。</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　第五十四条危险化学品事故造成环境污染的信息，由环境保护部门统一公布。</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">&nbsp;</span>\r\n</p>\r\n<p style=\"text-align:center;line-height:24px\">\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><strong><span style=\"font-size: 24px; font-family: 宋体; color: black; letter-spacing: 1px;\">第六章&nbsp;法律责任</span></strong></span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　第五十五条对生产、经营、储存、运输、使用危险化学品和处置废弃危险化学品依法实施监督管理的有关部门工作人员，有下列行为之一的，依法给予降级或者撤职的行政处分；触犯刑律的，依照刑法关于受贿罪、滥用职权罪、玩忽职守罪或者其他罪的规定，依法追究刑事责任：</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　（一）利用职务上的便利收受他人财物或者其他好处，对不符合本条例规定条件的涉及生产、经营、储存、运输、使用危险化学品和处置废弃危险化学品的事项予以批准或者许可的；</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　（二）发现未依法取得批准或者许可的单位和个人擅自从事有关活动或者接到举报后不予取缔或者不依法予以处理的；</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　（三）对已经依法取得批准或者许可的单位和个人不履行监督管理职责，发现其不再具备本条例规定的条件而不撤销原批准、许可或者发现违反本条例的行为不予查处的。</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　第五十六条发生危险化学品事故，有关部门未依照本条例的规定履行职责，组织实施救援或者采取必要措施，减少事故损失，防止事故蔓延、扩大，或者拖延、推诿的，对负有责任的主管人员和其他直接责任人员依法给予降级或者撤职的行政处分；触犯刑律的，依照刑法关于滥用职权罪、玩忽职守罪或者其他罪的规定，依法追究刑事责任。</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　第五十七条违反本条例的规定，有下列行为之一的，分别由工商行政管理部门、质检部门、负责危险化学品安全监督管理综合工作的部门依据各自的职权予以关闭或者责令停产停业整顿，责令无害化销毁国家明令禁止生产、经营、使用的危险化学品或者用剧毒化学品生产的灭鼠药以及其他可能进入人民日常生活的化学产品和日用化学品；有违法所得的，没收违法所得；违法所得10万元以上的，并处违法所得1倍以上5倍以下的罚款；没有违法所得或者违法所得不足10万元的，并处5万元以上50万元以下的罚款；触犯刑律的，对负有责任的主管人员和其他直接责任人员依照刑法关于危险物品肇事罪、非法经营罪或者其他罪的规定，依法追究刑事责任：</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　（一）未经批准或者未经工商登记注册，擅自从事危险化学品生产、储存的；</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　（二）未取得危险化学品生产许可证，擅自开工生产危险化学品的；</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　（三）未经审查批准，危险化学品生产、储存企业擅自改建、扩建的；</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　（四）未取得危险化学品经营许可证或者未经工商登记注册，擅自从事危险化学品经营的；</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　（五）生产、经营、使用国家明令禁止的危险化学品，或者用剧毒化学品生产灭鼠药以及其他可能进入人民日常生活的化学产品和日用化学品的。</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　第五十八条危险化学品单位违反本条例的规定，未根据危险化学品的种类、特性，在车间、库房等作业场所设置相应的监测、通风、防晒、调温、防火、灭火、防爆、泄压、防毒、消毒、中和、防潮、防雷、防静电、防腐、防渗漏、防护围堤或者隔离操作等安全设施、设备的，由负责危险化学品安全监督管理综合工作的部门或者公安部门依据各自的职权责令立即或者限期改正，处2万元以上10万元以下的罚款；触犯刑律的，对负有责任的主管人员和其他直接责任人员依照刑法关于危险物品肇事罪、重大责任事故罪或者其他罪的规定，依法追究刑事责任。</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　第五十九条违反本条例的规定，有下列行为之一的，由负责危险化学品安全监督管理综合工作的部门、质检部门或者交通部门依据各自的职权责令立即或者限期改正，处2万元以上20万元以下的罚款；逾期未改正的，责令停产停业整顿；触犯刑律的，对负有责任的主管人员和其他直接责任人员依照刑法关于危险物品肇事罪、生产销售伪劣商品罪或者其他罪的规定，依法追究刑事责任：</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　（一）未经定点，擅自生产危险化学品包装物、容器的；</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　（二）运输危险化学品的船舶及其配载的容器未按照国家关于船舶检验的规范进行生产，并经检验合格的；</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　（三）危险化学品包装的材质、型式、规格、方法和单件质量（重量）与所包装的危险化学品的性质和用途不相适应的；</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　（四）对重复使用的危险化学品的包装物、容器在使用前，不进行检查的；</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　（五）使用非定点企业生产的或者未经检测、检验合格的包装物、容器包装、盛装、运输危险化学品的。</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　第六十条危险化学品单位违反本条例的规定，有下列行为之一的，由负责危险化学品安全监督管理综合工作的部门责令立即或者限期改正，处1万元以上5万元以下的罚款；逾期不改正的，责令停产停业整顿：</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　（一）危险化学品生产企业未在危险化学品包装内附有与危险化学品完全一致的化学品安全技术说明书，或者未在包装（包括外包装件）上加贴、拴挂与包装内危险化学品完全一致的化学品安全标签的；</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　（二）危险化学品生产企业发现危险化学品有新的危害特性时，不立即公告并及时修订其安全技术说明书和安全标签的；</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　（三）危险化学品经营企业销售没有化学品安全技术说明书和安全标签的危险化学品的。</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　第六十一条危险化学品单位违反本条例的规定，有下列行为之一的，由负责危险化学品安全监督管理综合工作的部门或者公安部门依据各自的职权责令立即或者限期改正，处1万元以上5万元以下的罚款；逾期不改正的，由原发证机关吊销危险化学品生产许可证、经营许可证和营业执照；触犯刑律的，对负有责任的主管人员和其他直接责任人员依照刑法关于危险物品肇事罪、重大责任事故罪或者其他罪的规定，依法追究刑事责任：</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　（一）未对其生产、储存装置进行定期安全评价，并报所在地设区的市级人民政府负责危险化学品安全监督管理综合工作的部门备案，或者对安全评价中发现的存在现实危险的生产、储存装置不立即停止使用，予以更换或者修复，并采取相应的安全措施的；</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　（二）未在生产、储存和使用危险化学品场所设置通讯、报警装置，并保持正常适用状态的；</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　（三）危险化学品未储存在专用仓库内或者未设专人管理的；</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　（四）危险化学品出入库未进行核查登记或者入库后未定期检查的；</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　（五）危险化学品专用仓库不符合国家标准对安全、消防的要求，未设置明显标志，或者未对专用仓库的储存设备和安全设施定期检测的；</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　（六）危险化学品经销商店存放非民用小包装的危险化学品或者危险化学品民用小包装的存放量超过国家规定限量的；</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　（七）剧毒化学品以及构成重大危险源的其他危险化学品未在专用仓库内单独存放，或者未实行双人收发、双人保管，或者未将储存剧毒化学品以及构成重大危险源的其他危险化学品的数量、地点以及管理人员的情况，报当地公安部门和负责危险化学品安全监督管理综合工作的部门备案的；</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　（八）危险化学品生产单位不如实记录剧毒化学品的产量、流向、储存量和用途，或者未采取必要的保安措施防止剧毒化学品被盗、丢失、误售、误用，或者发生剧毒化学品被盗、丢失、误售、误用后不立即向当地公安部门报告的；</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　（九）危险化学品经营企业不记录剧毒化学品购买单位的名称、地址，购买人员的姓名、身份证号码及所购剧毒化学品的品名、数量、用途，或者不每天核对剧毒化学品的销售情况，或者发现被盗、丢失、误售不立即向当地公安部门报告的。</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　第六十二条危险化学品单位违反本条例的规定，在转产、停产、停业或者解散时未采取有效措施，处置危险化学品生产、储存设备、库存产品及生产原料的，由负责危险化学品安全监督管理综合工作的部门责令改正，处2万元以上10万元以下的罚款；触犯刑律的，对负有责任的主管人员和其他直接责任人员依照刑法关于重大环境污染事故罪、危险物品肇事罪或者其他罪的规定，依法追究刑事责任。</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　第六十三条违反本条例的规定，有下列行为之一的，由工商行政管理部门责令改正，有违法所得的，没收违法所得；违法所得5万元以上的，并处违法所得1倍以上5倍以下的罚款；没有违法所得或者违法所得不足5万元的，并处2万元以上20万元以下的罚款；不改正的，由原发证机关吊销生产许可证、经营许可证和营业执照；触犯刑律的，对负有责任的主管人员和其他直接责任人员依照刑法关于非法经营罪、危险物品肇事罪或者其他罪的规定，依法追究刑事责任：</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　（一）危险化学品经营企业从未取得危险化学品生产许可证或者危险化学品经营许可证的企业采购危险化学品的；</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　（二）危险化学品生产企业向未取得危险化学品经营许可证的经营单位销售其产品的；</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　（三）剧毒化学品经营企业向个人或者无购买凭证、准购证的单位销售剧毒化学品的。</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　第六十四条违反本条例的规定，伪造、变造、买卖、出借或者以其他方式转让剧毒化学品购买凭证、准购证以及其他有关证件，或者使用作废的上述有关证件的，由公安部门责令改正，处1万元以上5万元以下的罚款；触犯刑律的，对负有责任的主管人员和其他直接责任人员依照刑法关于伪造、变造、买卖国家机关公文、证件、印章罪或者其他罪的规定，依法追究刑事责任。</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　第六十五条违反本条例的规定，未取得危险化学品运输企业资质，擅自从事危险化学品公路、水路运输，有违法所得的，由交通部门没收违法所得；违法所得5万元以上的，并处违法所得1倍以上5倍以下的罚款；没有违法所得或者违法所得不足5万元的，处2万元以上20万元以下的罚款；触犯刑律的，对负有责任的主管人员和其他直接责任人员依照刑法关于危险物品肇事罪或者其他罪的规定，依法追究刑事责任。</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　第六十六条违反本条例的规定，有下列行为之一的，由交通部门处２万元以上10万元以下的罚款；触犯刑律的，依照刑法关于危险物品肇事罪或者其他罪的规定，依法追究刑事责任：</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　（一）从事危险化学品公路、水路运输的驾驶员、船员、装卸管理人员、押运人员未经考核合格，取得上岗资格证的；</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　（二）利用内河以及其他封闭水域等航运渠道运输剧毒化学品和国家禁止运输的其他危险化学品的；</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　（三）托运人未按照规定向交通部门办理水路运输手续，擅自通过水路运输剧毒化学品和国家禁止运输的其他危险化学品以外的危险化学品的；</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　（四）托运人托运危险化学品，不向承运人说明运输的危险化学品的品名、数量、危害、应急措施等情况，或者需要添加抑制剂或者稳定剂，交付托运时未添加的；</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　（五）运输、装卸危险化学品不符合国家有关法律、法规、规章的规定和国家标准，并按照危险化学品的特性采取必要安全防护措施的。</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　第六十七条违反本条例的规定，有下列行为之一的，由公安部门责令改正，处2万元以上10万元以下的罚款；触犯刑律的，依照刑法关于危险物品肇事罪、重大环境污染事故罪或者其他罪的规定，依法追究刑事责任：</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　（一）托运人未向公安部门申请领取剧毒化学品公路运输通行证，擅自通过公路运输剧毒化学品的；</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　（二）危险化学品运输企业运输危险化学品，不配备押运人员或者脱离押运人员监管，超装、超载，中途停车住宿或者遇有无法正常运输的情况，不向当地公安部门报告的；</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　（三）危险化学品运输企业运输危险化学品，未向公安部门报告，擅自进入危险化学品运输车辆禁止通行区域，或者进入禁止通行区域不遵守公安部门规定的行车时间和路线的；</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　（四）危险化学品运输企业运输剧毒化学品，在公路运输途中发生被盗、丢失、流散、泄露等情况，不立即向当地公安部门报告，并采取一切可能的警示措施的；</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　（五）托运人在托运的普通货物中夹带危险化学品或者将危险化学品匿报、谎报为普通货物托运的。</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　第六十八条违反本条例的规定，邮寄或者在邮件内夹带危险化学品，或者将危险化学品匿报、谎报为普通物品邮寄的，由公安部门处2000元以上2万元以下的罚款；触犯刑律的，依照刑法关于危险物品肇事罪或者其他罪的规定，依法追究刑事责任。</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　第六十九条危险化学品单位发生危险化学品事故，未按照本条例的规定立即组织救援，或者不立即向负责危险化学品安全监督管理综合工作的部门和公安、环境保护、质检部门报告，造成严重后果的，对负有责任的主管人员和其他直接责任人员依照刑法关于国有公司、企业工作人员失职罪或者其他罪的规定，依法追究刑事责任。</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　第七十条危险化学品单位发生危险化学品事故造成人员伤亡、财产损失的，应当依法承担赔偿责任；拒不承担赔偿责任或者其负责人逃匿的，依法拍卖其财产，用于赔偿。</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">&nbsp;</span>\r\n</p>\r\n<p style=\"text-align:center;line-height:24px\">\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><strong><span style=\"font-size: 24px; font-family: 宋体; color: black; letter-spacing: 1px;\">第七章&nbsp;附则</span></strong></span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　第七十一条监控化学品、属于药品的危险化学品和农药的安全管理，依照本条例的规定执行；国家另有规定的，依照其规定。</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　民用爆炸品、放射性物品、核能物质和城镇燃气的安全管理，不适用本条例。</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　第七十二条危险化学品的进出口管理依照国家有关规定执行；进口危险化学品的经营、储存、运输、使用和处置进口废弃危险化学品，依照本条例的规定执行。</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　第七十三条依照本条例的规定，对生产、经营、储存、运输、使用危险化学品和处置废弃危险化学品进行审批、许可并实施监督管理的国务院有关部门，应当根据本条例的规定制定并公布审批、许可的期限和程序。</span>\r\n</p>\r\n<p style=\"text-align:left;line-height:24px\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　本条例规定的国家标准和涉及危险化学品安全管理的国家有关规定，由国务院质检部门或者国务院有关部门分别依照国家标准化法律和其他有关法律、行政法规以及本条例的规定制定、调整并公布。</span>\r\n</p>\r\n<p style=\"text-align:left\">\r\n    <span style=\"color: black; letter-spacing: 1px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　第七十四条本条例自2002年3月15日起施行。1987年2月17日国务院发布的《化学危险物品安全管理条例》同时废止。</span>\r\n</p>\r\n<p>\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">&nbsp;</span>\r\n</p>\r\n<p>\r\n    <br/>\r\n</p>');
INSERT INTO `regulation_rules` VALUES ('3', '中华人民共和国病原微生物实验室生物安全管理条例', '2018-10-10', '<p style=\";margin-bottom:0;text-align:center;text-indent:28px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">（2004年11月5日起实行）</span>\r\n</p>\r\n<p style=\"text-indent:48px\">\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><strong><span style=\"font-size: 24px; font-family: 方正仿宋简体; color: black;\">第一章</span></strong><strong> </strong><strong><span style=\"font-size: 24px; font-family: 方正仿宋简体; color: black;\">总</span></strong><strong> </strong><strong><span style=\"font-size: 24px; font-family: 方正仿宋简体; color: black;\">则</span></strong></span>\r\n</p>\r\n<p style=\"text-indent:36px\">\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><strong><span style=\"font-size: 18px; font-family: 方正仿宋简体; color: black;\">第一条</span></strong></span>\r\n</p>\r\n<p style=\"text-indent:28px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">为了加强病原微生物实验室（以下称实验室）生物安全管理，保护实验室工作人员和公众的健康，制定本条例。</span>\r\n</p>\r\n<p style=\"text-indent:36px\">\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><strong><span style=\"font-size: 18px; font-family: 方正仿宋简体; color: black;\">第二条</span></strong></span>\r\n</p>\r\n<p style=\"text-indent:28px\">\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><span style=\"font-size: 14px; font-family: 方正仿宋简体; color: black;\">对</span><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px; color: black;\"><a href=\"http://baike.baidu.com/view/1256.htm\" target=\"_blank\" style=\"outline: none\"><span style=\"font-size: 16px; font-family: 方正仿宋简体; color: black;\">中华人民共和国</span></a></span><span style=\"font-size: 14px; font-family: 方正仿宋简体; color: black;\">境内的实验室及其从事实验活动的生物安全管理，适用本条例。</span> <span style=\"font-size: 14px; font-family: 方正仿宋简体; color: black;\">　　本条例所称病原微生物，是指能够使人或者动物致病的微生物。</span> <span style=\"font-size: 14px; font-family: 方正仿宋简体; color: black;\">　　本条例所称实验活动，是指实验室从事与病原微生物菌（毒）种、样本有关的研究、教学、检测、诊断等活动。</span></span>\r\n</p>\r\n<p style=\"text-indent:36px\">\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><strong><span style=\"font-size: 18px; font-family: 方正仿宋简体; color: black;\">第三条</span></strong></span>\r\n</p>\r\n<p style=\"text-indent:28px\">\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><span style=\"font-size: 14px; font-family: 方正仿宋简体; color: black;\">国务院卫生主管部门主管与人体健康有关的实验室及其实验活动的生物安全监督工作。</span> <span style=\"font-size: 14px; font-family: 方正仿宋简体; color: black;\">　　国务院兽医主管部门主管与动物有关的实验室及其实验活动的生物安全监督工作。</span> <span style=\"font-size: 14px; font-family: 方正仿宋简体; color: black;\">　　国务院其他有关部门在各自职责范围内负责实验室及其实验活动的生物安全管理工作。</span> <span style=\"font-size: 14px; font-family: 方正仿宋简体; color: black;\">　　县级以上地方人民政府及其有关部门在各自职责范围内负责实验室及其实验活动的生物安全管理工作。</span></span>\r\n</p>\r\n<p style=\"text-indent:36px\">\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><strong><span style=\"font-size: 18px; font-family: 方正仿宋简体; color: black;\">第四条</span></strong></span>\r\n</p>\r\n<p style=\"text-indent:28px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">国家对病原微生物实行分类管理，对实验室实行分级管理。</span>\r\n</p>\r\n<p style=\"text-indent:36px\">\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><strong><span style=\"font-size: 18px; font-family: 方正仿宋简体; color: black;\">第五条</span></strong></span>\r\n</p>\r\n<p style=\"text-indent:28px\">\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><span style=\"font-size: 14px; font-family: 方正仿宋简体; color: black;\">国家实行统一的</span><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px; color: black;\"><a href=\"http://baike.baidu.com/view/3121181.htm\" target=\"_blank\" style=\"outline: none\"><span style=\"font-size: 16px; font-family: 方正仿宋简体; color: black;\">实验室生物安全</span></a></span></span><span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">标准。实验室应当符合国家标准和要求。</span>\r\n</p>\r\n<p style=\"text-indent:36px\">\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><strong><span style=\"font-size: 18px; font-family: 方正仿宋简体; color: black;\">第六条</span></strong></span>\r\n</p>\r\n<p style=\"text-indent:28px\">\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><span style=\"font-size: 14px; font-family: 方正仿宋简体; color: black;\">实验室的设立单位及其主管部门负责实验室日常活动的管理，承担建立健全安全</span><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px; color: black;\"><a href=\"http://baike.baidu.com/view/673434.htm\" target=\"_blank\" style=\"outline: none\"><span style=\"font-size: 16px; font-family: 方正仿宋简体; color: black;\">管理制度</span></a></span></span><span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">，检查、维护实验设施、设备，控制实验室感染的职责。</span>\r\n</p>\r\n<p style=\"text-indent:48px\">\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><strong><span style=\"font-size: 24px; font-family: 方正仿宋简体; color: black;\">第二章</span></strong><strong> </strong><strong><span style=\"font-size: 24px; font-family: 方正仿宋简体; color: black;\">病原微生物的分类和管理</span></strong></span>\r\n</p>\r\n<p style=\"text-indent:36px\">\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><strong><span style=\"font-size: 18px; font-family: 方正仿宋简体; color: black;\">第七条</span></strong></span>\r\n</p>\r\n<p style=\"text-indent:28px\">\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><span style=\"font-size: 14px; font-family: 方正仿宋简体; color: black;\">国家根据病原微生物的传染性、感染后对个体或者群体的危害程度，将病原微生物分为四类：</span> <span style=\"font-size: 14px; font-family: 方正仿宋简体; color: black;\">　　第一类病原微生物，是指能够引起人类或者动物非常严重疾病的微生物，以及我国尚未发现或者已经宣布消灭的微生物。</span> <span style=\"font-size: 14px; font-family: 方正仿宋简体; color: black;\">　　第二类病原微生物，是指能够引起人类或者动物严重疾病，比较容易直接或者间接在人与人、动物与人、动物与动物间传播的微生物。</span> <span style=\"font-size: 14px; font-family: 方正仿宋简体; color: black;\">　　第三类病原微生物，是指能够引起人类或者动物疾病，但一般情况下对人、动物或者环境不构成严重危害，传播风险有限，实验室感染后很少引起严重疾病，并且具备有效治疗和预防措施的微生物。</span> <span style=\"font-size: 14px; font-family: 方正仿宋简体; color: black;\">　　第四类病原微生物，是指在通常情况下不会引起人类或者动物疾病的微生物。</span> <span style=\"font-size: 14px; font-family: 方正仿宋简体; color: black;\">　　第一类、第二类病原微生物统称为高致病性病原微生物。</span></span>\r\n</p>\r\n<p style=\"text-indent:36px\">\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><strong><span style=\"font-size: 18px; font-family: 方正仿宋简体; color: black;\">第八条</span></strong></span>\r\n</p>\r\n<p style=\"text-indent:28px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">人间传染的病原微生物名录由国务院卫生主管部门商国务院有关部门后制定、调整并予以公布；动物间传染的病原微生物名录由国务院兽医主管部门商国务院有关部门后制定、调整并予以公布。</span>\r\n</p>\r\n<p style=\"text-indent:36px\">\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><strong><span style=\"font-size: 18px; font-family: 方正仿宋简体; color: black;\">第九条</span></strong></span>\r\n</p>\r\n<p style=\"text-indent:28px\">\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><span style=\"font-size: 14px; font-family: 方正仿宋简体; color: black;\">采集病原微生物样本应当具备下列条件：</span> <span style=\"font-size: 14px; font-family: 方正仿宋简体; color: black;\">　　（一）具有与采集病原微生物样本所需要的生物安全防护水平相适应的设备；</span> <span style=\"font-size: 14px; font-family: 方正仿宋简体; color: black;\">　　（二）具有掌握相关专业知识和操作技能的工作人员；</span> <span style=\"font-size: 14px; font-family: 方正仿宋简体; color: black;\">　　（三）具有有效的防止病原微生物扩散和感染的措施；</span> <span style=\"font-size: 14px; font-family: 方正仿宋简体; color: black;\">　　（四）具有保证病原微生物样本质量的技术方法和手段。</span> <span style=\"font-size: 14px; font-family: 方正仿宋简体; color: black;\">　　采集高致病性病原微生物样本的工作人员在采集过程中应当防止病原微生物扩散和感染，并对样本的来源、采集过程和方法等作详细记录。</span></span>\r\n</p>\r\n<p style=\"text-indent:36px\">\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><strong><span style=\"font-size: 18px; font-family: 方正仿宋简体; color: black;\">第十条</span></strong></span>\r\n</p>\r\n<p style=\"text-indent:28px\">\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><span style=\"font-size: 14px; font-family: 方正仿宋简体; color: black;\">运输高致病性病原微生物菌（毒）种或者样本，应当通过</span><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px; color: black;\"><a href=\"http://baike.baidu.com/view/1470671.htm\" target=\"_blank\" style=\"outline: none\"><span style=\"font-size: 16px; font-family: 方正仿宋简体; color: black;\">陆路</span></a></span><span style=\"font-size: 14px; font-family: 方正仿宋简体; color: black;\">运输；没有陆路通道，必须经水路运输的，可以通过水路运输；紧急情况下或者需要将高致病性病原微生物菌（毒）种或者样本运往国外的，可以通过</span><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px; color: black;\"><a href=\"http://baike.baidu.com/view/1372889.htm\" target=\"_blank\" style=\"outline: none\"><span style=\"font-size: 16px; font-family: 方正仿宋简体; color: black;\">民用航空运输</span></a></span></span><span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">。</span>\r\n</p>\r\n<p style=\"text-indent:36px\">\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><strong><span style=\"font-size: 18px; font-family: 方正仿宋简体; color: black;\">第十一条</span></strong></span>\r\n</p>\r\n<p style=\"text-indent:28px\">\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><span style=\"font-size: 14px; font-family: 方正仿宋简体; color: black;\">运输高致病性病原微生物菌（毒）种或者样本，应当具备下列条件：</span> <span style=\"font-size: 14px; font-family: 方正仿宋简体; color: black;\">　　（一）运输目的、高致病性病原微生物的用途和接收单位符合国务院卫生主管部门或者兽医主管部门的规定；</span> <span style=\"font-size: 14px; font-family: 方正仿宋简体; color: black;\">　　（二）高致病性病原微生物菌（毒）种或者样本的容器应当密封，容器或者包装材料还应当符合防水、防破损、防外泄、耐高（低）温、耐高压的要求；</span> <span style=\"font-size: 14px; font-family: 方正仿宋简体; color: black;\">　　（三）容器或者包装材料上应当印有国务院卫生主管部门或者兽医主管部门规定的生物危险标识、警告用语和提示用语。</span> <span style=\"font-size: 14px; font-family: 方正仿宋简体; color: black;\">　　运输高致病性病原微生物菌（毒）种或者样本，应当经省级以上人民政府卫生主管部门或者兽医主管部门批准。在省、</span><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px; color: black;\"><a href=\"http://baike.baidu.com/view/54334.htm\" target=\"_blank\" style=\"outline: none\"><span style=\"font-size: 16px; font-family: 方正仿宋简体; color: black;\">自治区</span></a></span><span style=\"font-size: 14px; font-family: 方正仿宋简体; color: black;\">、直辖市</span><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px; color: black;\"><a href=\"http://baike.baidu.com/view/593053.htm\" target=\"_blank\" style=\"outline: none\"><span style=\"font-size: 16px; font-family: 方正仿宋简体; color: black;\">行政区域</span></a></span><span style=\"font-size: 14px; font-family: 方正仿宋简体; color: black;\">内运输的，由省、自治区、直辖市人民政府卫生主管部门或者兽医主管部门批准；需要</span><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px; color: black;\"><a href=\"http://baike.baidu.com/view/2729874.htm\" target=\"_blank\" style=\"outline: none\"><span style=\"font-size: 16px; font-family: 方正仿宋简体; color: black;\">跨省</span></a></span><span style=\"font-size: 14px; font-family: 方正仿宋简体; color: black;\">、自治区、直辖市运输或者运往国外的，由出发地的省、自治区、直辖市人民政府卫生主管部门或者兽医主管部门进行初审后，分别报国务院卫生主管部门或者兽医主管部门批准。</span> <span style=\"font-size: 14px; font-family: 方正仿宋简体; color: black;\">　　出入境检验检疫机构在检验检疫过程中需要运输病原微生物样本的，由国务院出入境检验检疫部门批准，并同时向国务院卫生主管部门或者兽医主管部门通报。</span> <span style=\"font-size: 14px; font-family: 方正仿宋简体; color: black;\">　　通过民用航空运输高致病性病原微生物菌（毒）种或者样本的，除依照本条第二款、第三款规定取得批准外，还应当经国务院民用航空主管部门批准。</span> <span style=\"font-size: 14px; font-family: 方正仿宋简体; color: black;\">　　有关主管部门应当对申请人提交的关于运输高致性病原微生物菌（毒）种或者样本的申请材料进行审查，对符合本条第一款规定条件的，应当即时批准。</span></span>\r\n</p>\r\n<p style=\"text-indent:36px\">\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><strong><span style=\"font-size: 18px; font-family: 方正仿宋简体; color: black;\">第十二条</span></strong></span>\r\n</p>\r\n<p style=\"text-indent:28px\">\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><span style=\"font-size: 14px; font-family: 方正仿宋简体; color: black;\">运输高致病性病原微生物菌（毒）种或者样本，应当由不少于２人的专人护送，并采取相应的防护措施。</span> <span style=\"font-size: 14px; font-family: 方正仿宋简体; color: black;\">　　有关单位或者个人不得通过公共电（汽）车和城市铁路运输病原微生物菌（毒）种或者样本。</span></span>\r\n</p>\r\n<p style=\"text-indent:36px\">\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><strong><span style=\"font-size: 18px; font-family: 方正仿宋简体; color: black;\">第十三条</span></strong></span>\r\n</p>\r\n<p style=\"text-indent:28px\">\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><span style=\"font-size: 14px; font-family: 方正仿宋简体; color: black;\">需要通过铁路、公路、民用航空等公共交通工具运输高致病性病原微生物菌（毒）种或者样本的，承运单位应当凭本条例第十一条规定的批准文件予以运输。</span> <span style=\"font-size: 14px; font-family: 方正仿宋简体; color: black;\">　　承运单位应当与护送人共同采取措施，确保所运输的高致病性病原微生物菌（毒）种或者样本的安全，严防发生被盗、被抢、丢失、泄漏事件。</span></span>\r\n</p>\r\n<p style=\"text-indent:36px\">\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><strong><span style=\"font-size: 18px; font-family: 方正仿宋简体; color: black;\">第十四条</span></strong></span>\r\n</p>\r\n<p style=\"text-indent:28px\">\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><span style=\"font-size: 14px; font-family: 方正仿宋简体; color: black;\">国务院卫生主管部门或者兽医主管部门指定的菌（毒）种保藏中心或者专业实验室（以下称保藏机构），承担集中储存病原微生物菌（毒）种和样本的任务。</span> <span style=\"font-size: 14px; font-family: 方正仿宋简体; color: black;\">　　保藏机构应当依照国务院卫生主管部门或者兽医主管部门的规定，储存实验室送交的病原微生物菌（毒）种和样本，并向实验室提供病原微生物菌（毒）种和样本。</span> <span style=\"font-size: 14px; font-family: 方正仿宋简体; color: black;\">　　保藏机构应当制定严格的安全保管制度，作好病原微生物菌（毒）种和样本进出和储存的记录，建立档案制度，并指定专人负责。对高致病性病原微生物菌（毒）种和样本应当设专库或者专柜单独储存。</span> <span style=\"font-size: 14px; font-family: 方正仿宋简体; color: black;\">　　保藏机构储存、提供病原微生物菌（毒）种和样本，不得收取任何费用，其经费由同级财政在单位预算中予以保障。</span> <span style=\"font-size: 14px; font-family: 方正仿宋简体; color: black;\">　　保藏机构的管理办法由国务院卫生主管部门会同国务院兽医主管部门制定。</span></span>\r\n</p>\r\n<p style=\"text-indent:36px\">\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><strong><span style=\"font-size: 18px; font-family: 方正仿宋简体; color: black;\">第十五条</span></strong></span>\r\n</p>\r\n<p style=\"text-indent:28px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">保藏机构应当凭实验室依照本条例的规定取得的从事高致病性病原微生物相关实验活动的批准文件，向实验室提供高致病性病原微生物菌（毒）种和样本，并予以登记。</span>\r\n</p>\r\n<p style=\"text-indent:36px\">\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><strong><span style=\"font-size: 18px; font-family: 方正仿宋简体; color: black;\">第十六条</span></strong></span>\r\n</p>\r\n<p style=\"text-indent:28px\">\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><span style=\"font-size: 14px; font-family: 方正仿宋简体; color: black;\">实验室在相关实验活动结束后，应当依照国务院卫生主管部门或者兽医主管部门的规定，及时将病原微生物菌（毒）种和样本就地销毁或者送交保藏机构保管。</span> <span style=\"font-size: 14px; font-family: 方正仿宋简体; color: black;\">　　保藏机构接受实验室送交的病原微生物菌（毒）种和样本，应当予以登记，并开具接收证明。</span></span>\r\n</p>\r\n<p style=\"text-indent:36px\">\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><strong><span style=\"font-size: 18px; font-family: 方正仿宋简体; color: black;\">第十七条</span></strong></span>\r\n</p>\r\n<p style=\"text-indent:28px\">\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><span style=\"font-size: 14px; font-family: 方正仿宋简体; color: black;\">高致病性病原微生物菌（毒）种或者样本在运输、储存中被盗、被抢、丢失、泄漏的，承运单位、护送人、保藏机构应当采取必要的控制措施，并在２小时内分别向承运单位的主管部门、护送人所在单位和保藏机构的主管部门报告，同时向所在地的县级人民政府卫生主管部门或者兽医主管部门报告，发生被盗、被抢、丢失的，还应当向公安机关报告；接到报告的卫生主管部门或者兽医主管部门应当在２小时内向本级人民政府报告，并同时向上级人民政府卫生主管部门或者兽医主管部门和国务院卫生主管部门或者兽医主管部门报告。</span> <span style=\"font-size: 14px; font-family: 方正仿宋简体; color: black;\">　　县级人民政府应当在接到报告后２小时内向设区的市级人民政府或者上一级人民政府报告；设区的市级人民政府应当在接到报告后２小时内向省、自治区、直辖市人民政府报告。省、自治区、直辖市人民政府应当在接到报告后１小时内，向国务院卫生主管部门或者兽医主管部门报告。</span> <span style=\"font-size: 14px; font-family: 方正仿宋简体; color: black;\">　　任何单位和个人发现高致病性病原微生物菌（毒）种或者样本的容器或者包装材料，应当及时向附近的卫生主管部门或者兽医主管部门报告；接到报告的卫生主管部门或者兽医主管部门应当及时组织调查核实，并依法采取必要的控制措施。</span></span>\r\n</p>\r\n<p style=\"text-indent:48px\">\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><strong><span style=\"font-size: 24px; font-family: 方正仿宋简体; color: black;\">实验室的设立与管理：</span></strong></span>\r\n</p>\r\n<p style=\"text-indent:36px\">\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><strong><span style=\"font-size: 18px; font-family: 方正仿宋简体; color: black;\">第十八条</span></strong></span>\r\n</p>\r\n<p style=\"text-indent:28px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">国家根据实验室对病原微生物的生物安全防护水平，并依照实验室生物安全国家标准的规定，将实验室分为一级、二级、三级、四级。</span>\r\n</p>\r\n<p style=\"text-indent:36px\">\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><strong><span style=\"font-size: 18px; font-family: 方正仿宋简体; color: black;\">第十九条</span></strong></span>\r\n</p>\r\n<p style=\"text-indent:28px\">\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><span style=\"font-size: 14px; font-family: 方正仿宋简体; color: black;\">新建、改建、扩建三级、四级实验室或者生产、进口移动式三级、四级实验室应当遵守下列规定：</span> <span style=\"font-size: 14px; font-family: 方正仿宋简体; color: black;\">　　（一）符合国家</span><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px; color: black;\"><a href=\"http://baike.baidu.com/view/682703.htm\" target=\"_blank\" style=\"outline: none\"><span style=\"font-size: 16px; font-family: 方正仿宋简体; color: black;\">生物安全实验室</span></a></span><span style=\"font-size: 14px; font-family: 方正仿宋简体; color: black;\">体系规划并依法履行有关审批手续；</span> <span style=\"font-size: 14px; font-family: 方正仿宋简体; color: black;\">　　（二）经国务院科技主管部门审查同意；</span> <span style=\"font-size: 14px; font-family: 方正仿宋简体; color: black;\">　　（三）符合国家生物安全实验室建筑技术规范；</span> <span style=\"font-size: 14px; font-family: 方正仿宋简体; color: black;\">　　（四）依照《中华人民共和国</span><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px; color: black;\"><a href=\"http://baike.baidu.com/view/95253.htm\" target=\"_blank\" style=\"outline: none\"><span style=\"font-size: 16px; font-family: 方正仿宋简体; color: black;\">环境影响评价</span></a></span><span style=\"font-size: 14px; font-family: 方正仿宋简体; color: black;\">法》的规定进行环境影响评价并经环境保护主管部门审查批准；</span> <span style=\"font-size: 14px; font-family: 方正仿宋简体; color: black;\">　　（五）生物安全防护级别与其拟从事的实验活动相适应。</span> <span style=\"font-size: 14px; font-family: 方正仿宋简体; color: black;\">　　前款规定所称国家生物安全实验室体系规划，由国务院投资主管部门会同国务院有关部门制定。制定国家生物安全实验室体系规划应当遵循总量控制、合理布局、资源共享的原则，并应当召开听证会或者论证会，听取公共卫生、环境保护、投资管理和实验室管理等方面专家的意见。</span></span>\r\n</p>\r\n<p style=\"text-indent:36px\">\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><strong><span style=\"font-size: 18px; font-family: 方正仿宋简体; color: black;\">第二十条</span></strong></span>\r\n</p>\r\n<p style=\"text-indent:28px\">\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><span style=\"font-size: 14px; font-family: 方正仿宋简体; color: black;\">三级、四级实验室应当通过</span><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px; color: black;\"><a href=\"http://baike.baidu.com/view/2262361.htm\" target=\"_blank\" style=\"outline: none\"><span style=\"font-size: 16px; font-family: 方正仿宋简体; color: black;\">实验室国家认可</span></a></span><span style=\"font-size: 14px; font-family: 方正仿宋简体; color: black;\">。</span> <span style=\"font-size: 14px; font-family: 方正仿宋简体; color: black;\">　　国务院认证认可监督管理部门确定的认可机构应当依照实验室生物安全国家标准以及本条例的有关规定，对三级、四级实验室进行认可；实验室通过认可的，颁发相应级别的生物安全实验室证书。证书有效期为５年。</span></span>\r\n</p>\r\n<p style=\"text-indent:36px\">\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><strong><span style=\"font-size: 18px; font-family: 方正仿宋简体; color: black;\">第二十一条</span></strong></span>\r\n</p>\r\n<p style=\"text-indent:28px\">\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><span style=\"font-size: 14px; font-family: 方正仿宋简体; color: black;\">一级、二级实验室不得从事高致病性病原微生物实验活动。三级、四级实验室从事高致病性病原微生物实验活动，应当具备下列条件：</span> <span style=\"font-size: 14px; font-family: 方正仿宋简体; color: black;\">　　（一）实验目的和拟从事的实验活动符合国务院卫生主管部门或者兽医主管部门的规定；</span> <span style=\"font-size: 14px; font-family: 方正仿宋简体; color: black;\">　　（二）通过实验室国家认可；</span> <span style=\"font-size: 14px; font-family: 方正仿宋简体; color: black;\">　　（三）具有与拟从事的实验活动相适应的工作人员；</span> <span style=\"font-size: 14px; font-family: 方正仿宋简体; color: black;\">　　（四）工程质量经建筑主管部门依法检测验收合格。</span> <span style=\"font-size: 14px; font-family: 方正仿宋简体; color: black;\">　　国务院卫生主管部门或者兽医主管部门依照各自职责对三级、四级实验室是否符合上述条件进行审查；对符合条件的，发给从事高致病性病原微生物实验活动的资格证书。</span></span>\r\n</p>\r\n<p style=\"text-indent:36px\">\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><strong><span style=\"font-size: 18px; font-family: 方正仿宋简体; color: black;\">第二十二条</span></strong></span>\r\n</p>\r\n<p style=\"text-indent:28px\">\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><span style=\"font-size: 14px; font-family: 方正仿宋简体; color: black;\">取得从事高致病性病原微生物实验活动资格证书的实验室，需要从事某种高致病性病原微生物或者疑似高致病性病原微生物实验活动的，应当依照国务院卫生主管部门或者兽医主管部门的规定报省级以上人民政府卫生主管部门或者兽医主管部门批准。实验活动结果以及工作情况应当向原批准部门报告。</span> <span style=\"font-size: 14px; font-family: 方正仿宋简体; color: black;\">　　实验室申报或者接受与高致病性病原微生物有关的科研项目，应当符合科研需要和生物安全要求，具有相应的生物安全防护水平，并经国务院卫生主管部门或者兽医主管部门同意。</span></span>\r\n</p>\r\n<p style=\"text-indent:36px\">\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><strong><span style=\"font-size: 18px; font-family: 方正仿宋简体; color: black;\">第二十三条</span></strong></span>\r\n</p>\r\n<p style=\"text-indent:28px\">\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><span style=\"font-size: 14px; font-family: 方正仿宋简体; color: black;\">出入境检验检疫机构、</span><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px; color: black;\"><a href=\"http://baike.baidu.com/view/2069608.htm\" target=\"_blank\" style=\"outline: none\"><span style=\"font-size: 16px; font-family: 方正仿宋简体; color: black;\">医疗卫生机构</span></a></span><span style=\"font-size: 14px; font-family: 方正仿宋简体; color: black;\">、动物防疫机构在实验室开展检测、诊断工作时，发现高致病性病原微生物或者疑似高致病性病原微生物，需要进一步从事这类高致病性病原微生物相关实验活动的，应当依照本条例的规定经批准同意，并在取得相应资格证书的实验室中进行。</span> <span style=\"font-size: 14px; font-family: 方正仿宋简体; color: black;\">　　专门从事检测、诊断的实验室应当严格依照国务院卫生主管部门或者兽医主管部门的规定，建立健全规章制度，保证实验室生物安全。</span></span>\r\n</p>\r\n<p style=\"text-indent:36px\">\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><strong><span style=\"font-size: 18px; font-family: 方正仿宋简体; color: black;\">第二十四条</span></strong></span>\r\n</p>\r\n<p style=\"text-indent:28px\">\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><span style=\"font-size: 14px; font-family: 方正仿宋简体; color: black;\">省级以上人民政府卫生主管部门或者兽医主管部门应当自收到需要从事高致病性病原微生物相关实验活动的申请之日起１５日内作出是否批准的决定。</span> <span style=\"font-size: 14px; font-family: 方正仿宋简体; color: black;\">　　对出入境检验检疫机构为了检验检疫工作的紧急需要，申请在实验室对高致病性病原微生物或者疑似高致病性病原微生物开展进一步实验活动的，省级以上人民政府卫生主管部门或者兽医主管部门应当自收到申请之时起２小时内作出是否批准的决定；２小时内未作出决定的，实验室可以从事相应的实验活动。</span> <span style=\"font-size: 14px; font-family: 方正仿宋简体; color: black;\">　　省级以上人民政府卫生主管部门或者兽医主管部门应当为申请人通过电报、电传、传真、电子数据交换和</span><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px; color: black;\"><a href=\"http://baike.baidu.com/view/1524.htm\" target=\"_blank\" style=\"outline: none\"><span style=\"font-size: 16px; font-family: 方正仿宋简体; color: black;\">电子邮件</span></a></span></span><span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">等方式提出申请提供方便。</span>\r\n</p>\r\n<p style=\"text-indent:36px\">\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><strong><span style=\"font-size: 18px; font-family: 方正仿宋简体; color: black;\">第二十五条</span></strong></span>\r\n</p>\r\n<p style=\"text-indent:28px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">新建、改建或者扩建一级、二级实验室，应当向设区的市级人民政府卫生主管部门或者兽医主管部门备案。设区的市级人民政府卫生主管部门或者兽医主管部门应当每年将备案情况汇总后报省、自治区、直辖市人民政府卫生主管部门或者兽医主管部门。</span>\r\n</p>\r\n<p style=\"text-indent:36px\">\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><strong><span style=\"font-size: 18px; font-family: 方正仿宋简体; color: black;\">第二十六条</span></strong></span>\r\n</p>\r\n<p style=\"text-indent:28px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">国务院卫生主管部门和兽医主管部门应当定期汇总并互相通报实验室数量和实验室设立、分布情况，以及取得从事高致病性病原微生物实验活动资格证书的三级、四级实验室及其从事相关实验活动的情况。</span>\r\n</p>\r\n<p style=\"text-indent:36px\">\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><strong><span style=\"font-size: 18px; font-family: 方正仿宋简体; color: black;\">第二十七条</span></strong></span>\r\n</p>\r\n<p style=\"text-indent:28px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">已经建成并通过实验室国家认可的三级、四级实验室应当向所在地的县级人民政府环境保护主管部门备案。环境保护主管部门依照法律、行政法规的规定对实验室排放的废水、废气和其他废物处置情况进行监督检查。</span>\r\n</p>\r\n<p style=\"text-indent:36px\">\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><strong><span style=\"font-size: 18px; font-family: 方正仿宋简体; color: black;\">第二十八条</span></strong></span>\r\n</p>\r\n<p style=\"text-indent:28px\">\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><span style=\"font-size: 14px; font-family: 方正仿宋简体; color: black;\">对我国尚未发现或者已经宣布消灭的病原微生物，任何单位和个人未经批准不得从事相关实验活动。</span> <span style=\"font-size: 14px; font-family: 方正仿宋简体; color: black;\">　　为了预防、控制传染病，需要从事前款所指病原微生物相关实验活动的，应当经国务院卫生主管部门或者兽医主管部门批准，并在批准部门指定的专业实验室中进行。</span></span>\r\n</p>\r\n<p style=\"text-indent:36px\">\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><strong><span style=\"font-size: 18px; font-family: 方正仿宋简体; color: black;\">第二十九条</span></strong></span>\r\n</p>\r\n<p style=\"text-indent:28px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">实验室使用新技术、新方法从事高致病性病原微生物相关实验活动的，应当符合防止高致病性病原微生物扩散、保证生物安全和操作者人身安全的要求，并经国家病原微生物实验室生物安全专家委员会论证；经论证可行的，方可使用。</span>\r\n</p>\r\n<p style=\"text-indent:36px\">\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><strong><span style=\"font-size: 18px; font-family: 方正仿宋简体; color: black;\">第三十条</span></strong></span>\r\n</p>\r\n<p style=\"text-indent:28px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">需要在动物体上从事高致病性病原微生物相关实验活动的，应当在符合动物实验室生物安全国家标准的三级以上实验室进行。</span>\r\n</p>\r\n<p style=\"text-indent:36px\">\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><strong><span style=\"font-size: 18px; font-family: 方正仿宋简体; color: black;\">第三十一条</span></strong></span>\r\n</p>\r\n<p style=\"text-indent:28px\">\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><span style=\"font-size: 14px; font-family: 方正仿宋简体; color: black;\">实验室的设立单位负责实验室的生物安全管理。</span> <span style=\"font-size: 14px; font-family: 方正仿宋简体; color: black;\">　　实验室的设立单位应当依照本条例的规定制定科学、严格的管理制度，并定期对有关生物安全规定的落实情况进行检查，定期对实验室设施、设备、材料等进行检查、维护和更新，以确保其符合国家标准。</span> <span style=\"font-size: 14px; font-family: 方正仿宋简体; color: black;\">　　实验室的设立单位及其主管部门应当加强对实验室日常活动的管理。</span></span>\r\n</p>\r\n<p style=\"text-indent:36px\">\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><strong><span style=\"font-size: 18px; font-family: 方正仿宋简体; color: black;\">第三十二条</span></strong></span>\r\n</p>\r\n<p style=\"text-indent:28px\">\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><span style=\"font-size: 14px; font-family: 方正仿宋简体; color: black;\">实验室负责人为实验室生物安全的</span><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px; color: black;\"><a href=\"http://baike.baidu.com/view/2190981.htm\" target=\"_blank\" style=\"outline: none\"><span style=\"font-size: 16px; font-family: 方正仿宋简体; color: black;\">第一责任</span></a></span><span style=\"font-size: 14px; font-family: 方正仿宋简体; color: black;\">人。</span> <span style=\"font-size: 14px; font-family: 方正仿宋简体; color: black;\">　　实验室从事实验活动应当严格遵守有关国家标准和实验室技术规范、操作规程。实验室负责人应当指定专人监督检查实验室技术规范和操作规程的落实情况。</span></span>\r\n</p>\r\n<p style=\"text-indent:36px\">\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><strong><span style=\"font-size: 18px; font-family: 方正仿宋简体; color: black;\">第三十三条</span></strong></span>\r\n</p>\r\n<p style=\"text-indent:28px\">\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><span style=\"font-size: 14px; font-family: 方正仿宋简体; color: black;\">从事高致病性病原微生物相关实验活动的实验室的设立单位，应当建立健全安全保卫制度，采取安全保卫措施，严防高致病性病原微生物被盗、被抢、丢失、泄漏，保障实验室及其病原微生物的安全。实验室发生高致病性病原微生物被盗、被抢、丢失、泄漏的，实验室的设立单位应当依照本条例第十七条的规定进行报告。</span> <span style=\"font-size: 14px; font-family: 方正仿宋简体; color: black;\">　　从事高致病性病原微生物相关实验活动的实验室应当向当地公安机关备案，并接受公安机关有关实验室安全保卫工作的监督指导。</span></span>\r\n</p>\r\n<p style=\"text-indent:36px\">\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><strong><span style=\"font-size: 18px; font-family: 方正仿宋简体; color: black;\">第三十四条</span></strong></span>\r\n</p>\r\n<p style=\"text-indent:28px\">\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><span style=\"font-size: 14px; font-family: 方正仿宋简体; color: black;\">实验室或者实验室的设立单位应当每年定期对工作人员进行培训，保证其掌握实验室技术规范、操作规程、生物安全防护知识和实际操作技能，并进行考核。工作人员经考核合格的，方可上岗。</span> <span style=\"font-size: 14px; font-family: 方正仿宋简体; color: black;\">　　从事高致病性病原微生物相关实验活动的实验室，应当每半年将培训、考核其工作人员的情况和实验室运行情况向省、自治区、直辖市人民政府卫生主管部门或者兽医主管部门报告。</span></span>\r\n</p>\r\n<p style=\"text-indent:36px\">\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><strong><span style=\"font-size: 18px; font-family: 方正仿宋简体; color: black;\">第三十五条</span></strong></span>\r\n</p>\r\n<p style=\"text-indent:28px\">\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><span style=\"font-size: 14px; font-family: 方正仿宋简体; color: black;\">从事高致病性病原微生物相关实验活动应当有２名以上的工作人员共同进行。</span> <span style=\"font-size: 14px; font-family: 方正仿宋简体; color: black;\">　　进入从事高致病性病原微生物相关实验活动的实验室的工作人员或者其他有关人员，应当经实验室负责人批准。实验室应当为其提供符合防护要求的防护用品并采取其他职业防护措施。从事高致病性病原微生物相关实验活动的实验室，还应当对实验室工作人员进行健康监测，每年组织对其进行体检，并建立健康档案；必要时，应当对实验室工作人员进行预防接种。</span></span>\r\n</p>\r\n<p style=\"text-indent:36px\">\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><strong><span style=\"font-size: 18px; font-family: 方正仿宋简体; color: black;\">第三十六条</span></strong></span>\r\n</p>\r\n<p style=\"text-indent:28px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">在同一个实验室的同一个独立安全区域内，只能同时从事一种高致病性病原微生物的相关实验活动。</span>\r\n</p>\r\n<p style=\"text-indent:36px\">\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><strong><span style=\"font-size: 18px; font-family: 方正仿宋简体; color: black;\">第三十七条</span></strong></span>\r\n</p>\r\n<p style=\"text-indent:28px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">实验室应当建立实验档案，记录实验室使用情况和安全监督情况。实验室从事高致病性病原微生物相关实验活动的实验档案保存期，不得少于２０年。</span>\r\n</p>\r\n<p style=\"text-indent:36px\">\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><strong><span style=\"font-size: 18px; font-family: 方正仿宋简体; color: black;\">第三十八条</span></strong></span>\r\n</p>\r\n<p style=\"text-indent:28px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">实验室应当依照环境保护的有关法律、行政法规和国务院有关部门的规定，对废水、废气以及其他废物进行处置，并制定相应的环境保护措施，防止环境污染。</span>\r\n</p>\r\n<p style=\"text-indent:36px\">\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><strong><span style=\"font-size: 18px; font-family: 方正仿宋简体; color: black;\">第三十九条</span></strong></span>\r\n</p>\r\n<p style=\"text-indent:28px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">三级、四级实验室应当在明显位置标示国务院卫生主管部门和兽医主管部门规定的生物危险标识和生物安全实验室级别标志。</span>\r\n</p>\r\n<p style=\"text-indent:36px\">\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><strong><span style=\"font-size: 18px; font-family: 方正仿宋简体; color: black;\">第四十条</span></strong></span>\r\n</p>\r\n<p style=\"text-indent:28px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">从事高致病性病原微生物相关实验活动的实验室应当制定实验室感染应急处置预案，并向该实验室所在地的省、自治区、直辖市人民政府卫生主管部门或者兽医主管部门备案。</span>\r\n</p>\r\n<p style=\"text-indent:36px\">\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><strong><span style=\"font-size: 18px; font-family: 方正仿宋简体; color: black;\">第四十一条</span></strong></span>\r\n</p>\r\n<p style=\"text-indent:28px\">\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><span style=\"font-size: 14px; font-family: 方正仿宋简体; color: black;\">国务院卫生主管部门和兽医主管部门会同国务院有关部门组织病原学、免疫学、检验医学、流行病学、预防兽医学、环境保护和实验室管理等方面的专家，组成国家病原微生物实验室生物安全专家委员会。该委员会承担从事高致病性病原微生物相关实验活动的实验室的设立与运行的生物安全评估和技术咨询、论证工作。</span> <span style=\"font-size: 14px; font-family: 方正仿宋简体; color: black;\">　　省、自治区、直辖市人民政府卫生主管部门和兽医主管部门会同同级人民政府有关部门组织病原学、免疫学、检验医学、流行病学、预防兽医学、环境保护和实验室管理等方面的专家，组成本地区病原微生物实验室生物安全专家委员会。该委员会承担本地区实验室设立和运行的技术咨询工作。</span></span>\r\n</p>\r\n<p style=\"text-indent:48px\">\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><strong><span style=\"font-size: 24px; font-family: 方正仿宋简体; color: black;\">第四章</span></strong><strong> </strong><strong><span style=\"font-size: 24px; font-family: 方正仿宋简体; color: black;\">实验室感染控制</span></strong></span>\r\n</p>\r\n<p style=\"text-indent:36px\">\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><strong><span style=\"font-size: 18px; font-family: 方正仿宋简体; color: black;\">第四十二条</span></strong></span>\r\n</p>\r\n<p style=\"text-indent:28px\">\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><span style=\"font-size: 14px; font-family: 方正仿宋简体; color: black;\">实验室的设立单位应当指定专门的机构或者人员承担实验室感染控制工作，定期检查实验室的生物安全防护、病原微生物菌（毒）种和样本保存与使用、安全操作、实验室排放的废水和废气以及其他废物处置等规章制度的实施情况。</span> <span style=\"font-size: 14px; font-family: 方正仿宋简体; color: black;\">　　负责实验室感染控制工作的机构或者人员应当具有与该实验室中的病原微生物有关的传染病防治知识，并定期调查、了解实验室工作人员的健康状况。</span></span>\r\n</p>\r\n<p style=\"text-indent:36px\">\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><strong><span style=\"font-size: 18px; font-family: 方正仿宋简体; color: black;\">第四十三条</span></strong></span>\r\n</p>\r\n<p style=\"text-indent:28px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">实验室工作人员出现与本实验室从事的高致病性病原微生物相关实验活动有关的感染临床症状或者体征时，实验室负责人应当向负责实验室感染控制工作的机构或者人员报告，同时派专人陪同及时就诊；实验室工作人员应当将近期所接触的病原微生物的种类和危险程度如实告知诊治医疗机构。接诊的医疗机构应当及时救治；不具备相应救治条件的，应当依照规定将感染的实验室工作人员转诊至具备相应传染病救治条件的医疗机构；具备相应传染病救治条件的医疗机构应当接诊治疗，不得拒绝救治。</span>\r\n</p>\r\n<p style=\"text-indent:36px\">\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><strong><span style=\"font-size: 18px; font-family: 方正仿宋简体; color: black;\">第四十四条</span></strong></span>\r\n</p>\r\n<p style=\"text-indent:28px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">实验室发生高致病性病原微生物泄漏时，实验室工作人员应当立即采取控制措施，防止高致病性病原微生物扩散，并同时向负责实验室感染控制工作的机构或者人员报告。</span>\r\n</p>\r\n<p style=\"text-indent:36px\">\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><strong><span style=\"font-size: 18px; font-family: 方正仿宋简体; color: black;\">第四十五条</span></strong></span>\r\n</p>\r\n<p style=\"text-indent:28px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">负责实验室感染控制工作的机构或者人员接到本条例第四十三条、第四十四条规定的报告后，应当立即启动实验室感染应急处置预案，并组织人员对该实验室生物安全状况等情况进行调查；确认发生实验室感染或者高致病性病原微生物泄漏的，应当依照本条例第十七条的规定进行报告，并同时采取控制措施，对有关人员进行医学观察或者隔离治疗，封闭实验室，防止扩散。</span>\r\n</p>\r\n<p style=\"text-indent:36px\">\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><strong><span style=\"font-size: 18px; font-family: 方正仿宋简体; color: black;\">第四十六条</span></strong></span>\r\n</p>\r\n<p style=\"text-indent:28px\">\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><span style=\"font-size: 14px; font-family: 方正仿宋简体; color: black;\">卫生主管部门或者兽医主管部门接到关于实验室发生工作人员感染事故或者病原微生物泄漏事件的报告，或者发现实验室从事病原微生物相关实验活动造成实验室感染事故的，应当立即组织疾病预防控制机构、动物防疫监督机构和医疗机构以及其他有关机构依法采取下列预防、控制措施：</span> <span style=\"font-size: 14px; font-family: 方正仿宋简体; color: black;\">　　（一）封闭被病原微生物污染的实验室或者可能造成病原微生物扩散的场所；</span> <span style=\"font-size: 14px; font-family: 方正仿宋简体; color: black;\">　　（二）开展</span><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px; color: black;\"><a href=\"http://baike.baidu.com/view/1907786.htm\" target=\"_blank\" style=\"outline: none\"><span style=\"font-size: 16px; font-family: 方正仿宋简体; color: black;\">流行病学调查</span></a></span><span style=\"font-size: 14px; font-family: 方正仿宋简体; color: black;\">；</span> <span style=\"font-size: 14px; font-family: 方正仿宋简体; color: black;\">　　（三）对病人进行隔离治疗，对相关人员进行医学检查；</span> <span style=\"font-size: 14px; font-family: 方正仿宋简体; color: black;\">　　（四）对密切接触者进行医学观察；</span> <span style=\"font-size: 14px; font-family: 方正仿宋简体; color: black;\">　　（五）进行现场消毒；</span> <span style=\"font-size: 14px; font-family: 方正仿宋简体; color: black;\">　　（六）对染疫或者疑似染疫的动物采取隔离、扑杀等措施；</span> <span style=\"font-size: 14px; font-family: 方正仿宋简体; color: black;\">　　（七）其他需要采取的预防、控制措施。</span></span>\r\n</p>\r\n<p style=\"text-indent:36px\">\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><strong><span style=\"font-size: 18px; font-family: 方正仿宋简体; color: black;\">第四十七条</span></strong></span>\r\n</p>\r\n<p style=\"text-indent:28px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">医疗机构或者兽医医疗机构及其执行职务的医务人员发现由于实验室感染而引起的与高致病性病原微生物相关的传染病病人、疑似传染病病人或者患有疫病、疑似患有疫病的动物，诊治的医疗机构或者兽医医疗机构应当在２小时内报告所在地的县级人民政府卫生主管部门或者兽医主管部门；接到报告的卫生主管部门或者兽医主管部门应当在２小时内通报实验室所在地的县级人民政府卫生主管部门或者兽医主管部门。接到通报的卫生主管部门或者兽医主管部门应当依照本条例第四十六条的规定采取预防、控制措施。</span>\r\n</p>\r\n<p style=\"text-indent:36px\">\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><strong><span style=\"font-size: 18px; font-family: 方正仿宋简体; color: black;\">第四十八条</span></strong></span>\r\n</p>\r\n<p style=\"text-indent:28px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">发生病原微生物扩散，有可能造成传染病暴发、流行时，县级以上人民政府卫生主管部门或者兽医主管部门应当依照有关法律、行政法规的规定以及实验室感染应急处置预案进行处理。</span>\r\n</p>\r\n<p style=\"text-indent:48px\">\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><strong><span style=\"font-size: 24px; font-family: 方正仿宋简体; color: black;\">第五章</span></strong><strong> </strong><strong><span style=\"font-size: 24px; font-family: 方正仿宋简体; color: black;\">监督管理</span></strong></span>\r\n</p>\r\n<p style=\"text-indent:36px\">\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><strong><span style=\"font-size: 18px; font-family: 方正仿宋简体; color: black;\">第四十九条</span></strong></span>\r\n</p>\r\n<p style=\"text-indent:28px\">\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><span style=\"font-size: 14px; font-family: 方正仿宋简体; color: black;\">县级以上地方人民政府卫生主管部门、兽医主管部门依照各自分工，履行下列职责：</span> <span style=\"font-size: 14px; font-family: 方正仿宋简体; color: black;\">　　（一）对病原微生物菌（毒）种、样本的采集、运输、储存进行监督检查；</span> <span style=\"font-size: 14px; font-family: 方正仿宋简体; color: black;\">　　（二）对从事高致病性病原微生物相关实验活动的实验室是否符合本条例规定的条件进行监督检查；</span> <span style=\"font-size: 14px; font-family: 方正仿宋简体; color: black;\">　　（三）对实验室或者实验室的设立单位培训、考核其工作人员以及上岗人员的情况进行监督检查；</span> <span style=\"font-size: 14px; font-family: 方正仿宋简体; color: black;\">　　（四）对实验室是否按照有关国家标准、技术规范和操作规程从事病原微生物相关实验活动进行监督检查。</span> <span style=\"font-size: 14px; font-family: 方正仿宋简体; color: black;\">　　县级以上地方人民政府卫生主管部门、兽医主管部门，应当主要通过检查反映实验室执行国家有关法律、行政法规以及国家标准和要求的记录、档案、报告，切实履行监督管理职责。</span></span>\r\n</p>\r\n<p style=\"text-indent:36px\">\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><strong><span style=\"font-size: 18px; font-family: 方正仿宋简体; color: black;\">第五十条</span></strong></span>\r\n</p>\r\n<p style=\"text-indent:28px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">县级以上人民政府卫生主管部门、兽医主管部门、环境保护主管部门在履行监督检查职责时，有权进入被检查单位和病原微生物泄漏或者扩散现场调查取证、采集样品，查阅复制有关资料。需要进入从事高致病性病原微生物相关实验活动的实验室调查取证、采集样品的，应当指定或者委托专业机构实施。被检查单位应当予以配合，不得拒绝、阻挠。</span>\r\n</p>\r\n<p style=\"text-indent:36px\">\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><strong><span style=\"font-size: 18px; font-family: 方正仿宋简体; color: black;\">第五十一条</span></strong></span>\r\n</p>\r\n<p style=\"text-indent:28px\">\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><span style=\"font-size: 14px; font-family: 方正仿宋简体; color: black;\">国务院认证认可监督管理部门依照《</span><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px; color: black;\"><a href=\"http://baike.baidu.com/view/136611.htm\" target=\"_blank\" style=\"outline: none\"><span style=\"font-size: 16px; font-family: 方正仿宋简体; color: black;\">中华人民共和国认证认可条例</span></a></span></span><span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">》的规定对实验室认可活动进行监督检查。</span>\r\n</p>\r\n<p style=\"text-indent:36px\">\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><strong><span style=\"font-size: 18px; font-family: 方正仿宋简体; color: black;\">第五十二条</span></strong></span>\r\n</p>\r\n<p style=\"text-indent:28px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">卫生主管部门、兽医主管部门、环境保护主管部门应当依据法定的职权和程序履行职责，做到公正、公平、公开、文明、高效。</span>\r\n</p>\r\n<p style=\"text-indent:36px\">\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><strong><span style=\"font-size: 18px; font-family: 方正仿宋简体; color: black;\">第五十三条</span></strong></span>\r\n</p>\r\n<p style=\"text-indent:28px\">\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><span style=\"font-size: 14px; font-family: 方正仿宋简体; color: black;\">卫生主管部门、兽医主管部门、环境保护主管部门的执法人员执行职务时，应当有２名以上执法人员参加，出示执法证件，并依照规定填写执法文书。</span> <span style=\"font-size: 14px; font-family: 方正仿宋简体; color: black;\">　　现场检查笔录、采样记录等文书经核对无误后，应当由执法人员和被检查人、被采样人签名。被检查人、被采样人拒绝签名的，执法人员应当在自己签名后注明情况。</span></span>\r\n</p>\r\n<p style=\"text-indent:36px\">\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><strong><span style=\"font-size: 18px; font-family: 方正仿宋简体; color: black;\">第五十四条</span></strong></span>\r\n</p>\r\n<p style=\"text-indent:28px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">卫生主管部门、兽医主管部门、环境保护主管部门及其执法人员执行职务，应当自觉接受社会和公民的监督。公民、法人和其他组织有权向上级人民政府及其卫生主管部门、兽医主管部门、环境保护主管部门举报地方人民政府及其有关主管部门不依照规定履行职责的情况。接到举报的有关人民政府或者其卫生主管部门、兽医主管部门、环境保护主管部门，应当及时调查处理。</span>\r\n</p>\r\n<p style=\"text-indent:36px\">\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><strong><span style=\"font-size: 18px; font-family: 方正仿宋简体; color: black;\">第五十五条</span></strong></span>\r\n</p>\r\n<p style=\"text-indent:28px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">上级人民政府卫生主管部门、兽医主管部门、环境保护主管部门发现属于下级人民政府卫生主管部门、兽医主管部门、环境保护主管部门职责范围内需要处理的事项的，应当及时告知该部门处理；下级人民政府卫生主管部门、兽医主管部门、环境保护主管部门不及时处理或者不积极履行本部门职责的，上级人民政府卫生主管部门、兽医主管部门、环境保护主管部门应当责令其限期改正；逾期不改正的，上级人民政府卫生主管部门、兽医主管部门、环境保护主管部门有权直接予以处理。</span>\r\n</p>\r\n<p style=\"text-indent:48px\">\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><strong><span style=\"font-size: 24px; font-family: 方正仿宋简体; color: black;\">第六章</span></strong><strong> </strong><strong><span style=\"font-size: 24px; font-family: 方正仿宋简体; color: black;\">法律责任</span></strong></span>\r\n</p>\r\n<p style=\"text-indent:36px\">\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><strong><span style=\"font-size: 18px; font-family: 方正仿宋简体; color: black;\">第五十六条</span></strong></span>\r\n</p>\r\n<p style=\"text-indent:28px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">三级、四级实验室未依照本条例的规定取得从事高致病性病原微生物实验活动的资格证书，或者已经取得相关资格证书但是未经批准从事某种高致病性病原微生物或者疑似高致病性病原微生物实验活动的，由县级以上地方人民政府卫生主管部门、兽医主管部门依照各自职责，责令停止有关活动，监督其将用于实验活动的病原微生物销毁或者送交保藏机构，并给予警告；造成传染病传播、流行或者其他严重后果的，由实验室的设立单位对主要负责人、直接负责的主管人员和其他直接责任人员，依法给予撤职、开除的处分；有资格证书的，应当吊销其资格证书；构成犯罪的，依法追究刑事责任。</span>\r\n</p>\r\n<p style=\"text-indent:36px\">\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><strong><span style=\"font-size: 18px; font-family: 方正仿宋简体; color: black;\">第五十七条</span></strong></span>\r\n</p>\r\n<p style=\"text-indent:28px\">\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><span style=\"font-size: 14px; font-family: 方正仿宋简体; color: black;\">卫生主管部门或者兽医主管部门违反本条例的规定，准予不符合本条例规定条件的实验室从事高致病性病原微生物相关实验活动的，由作出批准决定的卫生主管部门或者兽医主管部门撤销原批准决定，责令有关实验室立即停止有关活动，并监督其将用于实验活动的病原微生物销毁或者送交保藏机构，对直接负责的主管人员和其他直接责任人员依法给予行政处分；构成犯罪的，依法追究刑事责任。</span> <span style=\"font-size: 14px; font-family: 方正仿宋简体; color: black;\">　　因违法作出批准决定给当事人的合法权益造成损害的，作出批准决定的卫生主管部门或者兽医主管部门应当依法承担赔偿责任。</span></span>\r\n</p>\r\n<p style=\"text-indent:36px\">\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><strong><span style=\"font-size: 18px; font-family: 方正仿宋简体; color: black;\">第五十八条</span></strong></span>\r\n</p>\r\n<p style=\"text-indent:28px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">卫生主管部门或者兽医主管部门对符合法定条件的实验室不颁发从事高致病性病原微生物实验活动的资格证书，或者对出入境检验检疫机构为了检验检疫工作的紧急需要，申请在实验室对高致病性病原微生物或者疑似高致病性病原微生物开展进一步检测活动，不在法定期限内作出是否批准决定的，由其上级行政机关或者监察机关责令改正，给予警告；造成传染病传播、流行或者其他严重后果的，对直接负责的主管人员和其他直接责任人员依法给予撤职、开除的行政处分；构成犯罪的，依法追究刑事责任。</span>\r\n</p>\r\n<p style=\"text-indent:36px\">\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><strong><span style=\"font-size: 18px; font-family: 方正仿宋简体; color: black;\">第五十九条</span></strong></span>\r\n</p>\r\n<p style=\"text-indent:28px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">违反本条例规定，在不符合相应生物安全要求的实验室从事病原微生物相关实验活动的，由县级以上地方人民政府卫生主管部门、兽医主管部门依照各自职责，责令停止有关活动，监督其将用于实验活动的病原微生物销毁或者送交保藏机构，并给予警告；造成传染病传播、流行或者其他严重后果的，由实验室的设立单位对主要负责人、直接负责的主管人员和其他直接责任人员，依法给予撤职、开除的处分；构成犯罪的，依法追究刑事责任。</span>\r\n</p>\r\n<p style=\"text-indent:36px\">\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><strong><span style=\"font-size: 18px; font-family: 方正仿宋简体; color: black;\">第六十条</span></strong></span>\r\n</p>\r\n<p style=\"text-indent:28px\">\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><span style=\"font-size: 14px; font-family: 方正仿宋简体; color: black;\">实验室有下列行为之一的，由县级以上地方人民政府卫生主管部门、兽医主管部门依照各自职责，责令限期改正，给予警告；逾期不改正的，由实验室的设立单位对主要负责人、直接负责的主管人员和其他直接责任人员，依法给予撤职、开除的处分；有许可证件的，并由原发证部门吊销有关许可证件：</span> <span style=\"font-size: 14px; font-family: 方正仿宋简体; color: black;\">　　（一）未依照规定在明显位置标示国务院卫生主管部门和兽医主管部门规定的生物危险标识和生物安全实验室级别标志的；</span> <span style=\"font-size: 14px; font-family: 方正仿宋简体; color: black;\">　　（二）未向原批准部门报告实验活动结果以及工作情况的；</span> <span style=\"font-size: 14px; font-family: 方正仿宋简体; color: black;\">　　（三）未依照规定采集病原微生物样本，或者对所采集样本的来源、采集过程和方法等未作详细记录的；</span> <span style=\"font-size: 14px; font-family: 方正仿宋简体; color: black;\">　　（四）新建、改建或者扩建一级、二级实验室未向设区的市级人民政府卫生主管部门或者兽医主管部门备案的；</span> <span style=\"font-size: 14px; font-family: 方正仿宋简体; color: black;\">　　（五）未依照规定定期对工作人员进行培训，或者工作人员考核不合格允许其上岗，或者批准未采取防护措施的人员进入实验室的；</span> <span style=\"font-size: 14px; font-family: 方正仿宋简体; color: black;\">　　（六）实验室工作人员未遵守实验室生物安全技术规范和操作规程的；</span> <span style=\"font-size: 14px; font-family: 方正仿宋简体; color: black;\">　　（七）未依照规定建立或者保存实验档案的；</span> <span style=\"font-size: 14px; font-family: 方正仿宋简体; color: black;\">　　（八）未依照规定制定实验室感染应急处置预案并备案的。</span></span>\r\n</p>\r\n<p style=\"text-indent:36px\">\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><strong><span style=\"font-size: 18px; font-family: 方正仿宋简体; color: black;\">第六十一条</span></strong></span>\r\n</p>\r\n<p style=\"text-indent:28px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">经依法批准从事高致病性病原微生物相关实验活动的实验室的设立单位未建立健全安全保卫制度，或者未采取安全保卫措施的，由县级以上地方人民政府卫生主管部门、兽医主管部门依照各自职责，责令限期改正；逾期不改正，导致高致病性病原微生物菌（毒）种、样本被盗、被抢或者造成其他严重后果的，由原发证部门吊销该实验室从事高致病性病原微生物相关实验活动的资格证书；造成传染病传播、流行的，该实验室设立单位的主管部门还应当对该实验室的设立单位的直接负责的主管人员和其他直接责任人员，依法给予降级、撤职、开除的处分；构成犯罪的，依法追究刑事责任。</span>\r\n</p>\r\n<p style=\"text-indent:36px\">\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><strong><span style=\"font-size: 18px; font-family: 方正仿宋简体; color: black;\">第六十二条</span></strong></span>\r\n</p>\r\n<p style=\"text-indent:28px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">未经批准运输高致病性病原微生物菌（毒）种或者样本，或者承运单位经批准运输高致病性病原微生物菌（毒）种或者样本未履行保护义务，导致高致病性病原微生物菌（毒）种或者样本被盗、被抢、丢失、泄漏的，由县级以上地方人民政府卫生主管部门、兽医主管部门依照各自职责，责令采取措施，消除隐患，给予警告；造成传染病传播、流行或者其他严重后果的，由托运单位和承运单位的主管部门对主要负责人、直接负责的主管人员和其他直接责任人员，依法给予撤职、开除的处分；构成犯罪的，依法追究刑事责任。</span>\r\n</p>\r\n<p style=\"text-indent:36px\">\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><strong><span style=\"font-size: 18px; font-family: 方正仿宋简体; color: black;\">第六十三条</span></strong></span>\r\n</p>\r\n<p style=\"text-indent:28px\">\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><span style=\"font-size: 14px; font-family: 方正仿宋简体; color: black;\">有下列行为之一的，由实验室所在地的设区的市级以上地方人民政府卫生主管部门、兽医主管部门依照各自职责，责令有关单位立即停止违法活动，监督其将病原微生物销毁或者送交保藏机构；造成传染病传播、流行或者其他严重后果的，由其所在单位或者其</span><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px; color: black;\"><a href=\"http://baike.baidu.com/view/2300916.htm\" target=\"_blank\" style=\"outline: none\"><span style=\"font-size: 16px; font-family: 方正仿宋简体; color: black;\">上级主管部门</span></a></span><span style=\"font-size: 14px; font-family: 方正仿宋简体; color: black;\">对主要负责人、直接负责的主管人员和其他直接责任人员，依法给予撤职、开除的处分；有许可证件的，并由原发证部门吊销有关许可证件；构成犯罪的，依法追究刑事责任：</span> <span style=\"font-size: 14px; font-family: 方正仿宋简体; color: black;\">　　（一）实验室在相关实验活动结束后，未依照规定及时将病原微生物菌（毒）种和样本就地销毁或者送交保藏机构保管的；</span> <span style=\"font-size: 14px; font-family: 方正仿宋简体; color: black;\">　　（二）实验室使用新技术、新方法从事高致病性病原微生物相关实验活动未经国家病原微生物实验室生物安全专家委员会论证的；</span> <span style=\"font-size: 14px; font-family: 方正仿宋简体; color: black;\">　　（三）未经批准擅自从事在我国尚未发现或者已经宣布消灭的病原微生物相关实验活动的；</span> <span style=\"font-size: 14px; font-family: 方正仿宋简体; color: black;\">　　（四）在未经指定的专业实验室从事在我国尚未发现或者已经宣布消灭的病原微生物相关实验活动的；</span> <span style=\"font-size: 14px; font-family: 方正仿宋简体; color: black;\">　　（五）在同一个实验室的同一个独立安全区域内同时从事两种或者两种以上高致病性病原微生物的相关实验活动的。</span></span>\r\n</p>\r\n<p style=\"text-indent:36px\">\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><strong><span style=\"font-size: 18px; font-family: 方正仿宋简体; color: black;\">第六十四条</span></strong></span>\r\n</p>\r\n<p style=\"text-indent:28px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">认可机构对不符合实验室生物安全国家标准以及本条例规定条件的实验室予以认可，或者对符合实验室生物安全国家标准以及本条例规定条件的实验室不予认可的，由国务院认证认可监督管理部门责令限期改正，给予警告；造成传染病传播、流行或者其他严重后果的，由国务院认证认可监督管理部门撤销其认可资格，有上级主管部门的，由其上级主管部门对主要负责人、直接负责的主管人员和其他直接责任人员依法给予撤职、开除的处分；构成犯罪的，依法追究刑事责任。</span>\r\n</p>\r\n<p style=\"text-indent:36px\">\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><strong><span style=\"font-size: 18px; font-family: 方正仿宋简体; color: black;\">第六十五条</span></strong></span>\r\n</p>\r\n<p style=\"text-indent:28px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">实验室工作人员出现该实验室从事的病原微生物相关实验活动有关的感染临床症状或者体征，以及实验室发生高致病性病原微生物泄漏时，实验室负责人、实验室工作人员、负责实验室感染控制的专门机构或者人员未依照规定报告，或者未依照规定采取控制措施的，由县级以上地方人民政府卫生主管部门、兽医主管部门依照各自职责，责令限期改正，给予警告；造成传染病传播、流行或者其他严重后果的，由其设立单位对实验室主要负责人、直接负责的主管人员和其他直接责任人员，依法给予撤职、开除的处分；有许可证件的，并由原发证部门吊销有关许可证件；构成犯罪的，依法追究刑事责任。</span>\r\n</p>\r\n<p style=\"text-indent:36px\">\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><strong><span style=\"font-size: 18px; font-family: 方正仿宋简体; color: black;\">第六十六条</span></strong></span>\r\n</p>\r\n<p style=\"text-indent:28px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">拒绝接受卫生主管部门、兽医主管部门依法开展有关高致病性病原微生物扩散的调查取证、采集样品等活动或者依照本条例规定采取有关预防、控制措施的，由县级以上人民政府卫生主管部门、兽医主管部门依照各自职责，责令改正，给予警告；造成传染病传播、流行以及其他严重后果的，由实验室的设立单位对实验室主要负责人、直接负责的主管人员和其他直接责任人员，依法给予降级、撤职、开除的处分；有许可证件的，并由原发证部门吊销有关许可证件；构成犯罪的，依法追究刑事责任。</span>\r\n</p>\r\n<p style=\"text-indent:36px\">\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><strong><span style=\"font-size: 18px; font-family: 方正仿宋简体; color: black;\">第六十七条</span></strong></span>\r\n</p>\r\n<p style=\"text-indent:28px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">发生病原微生物被盗、被抢、丢失、泄漏，承运单位、护送人、保藏机构和实验室的设立单位未依照本条例的规定报告的，由所在地的县级人民政府卫生主管部门或者兽医主管部门给予警告；造成传染病传播、流行或者其他严重后果的，由实验室的设立单位或者承运单位、保藏机构的上级主管部门对主要负责人、直接负责的主管人员和其他直接责任人员，依法给予撤职、开除的处分；构成犯罪的，依法追究刑事责任。</span>\r\n</p>\r\n<p style=\"text-indent:36px\">\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><strong><span style=\"font-size: 18px; font-family: 方正仿宋简体; color: black;\">第六十八条</span></strong></span>\r\n</p>\r\n<p style=\"text-indent:28px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">保藏机构未依照规定储存实验室送交的菌（毒）种和样本，或者未依照规定提供菌（毒）种和样本的，由其指定部门责令限期改正，收回违法提供的菌（毒）种和样本，并给予警告；造成传染病传播、流行或者其他严重后果的，由其所在单位或者其上级主管部门对主要负责人、直接负责的主管人员和其他直接责任人员，依法给予撤职、开除的处分；构成犯罪的，依法追究刑事责任。</span>\r\n</p>\r\n<p style=\"text-indent:36px\">\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><strong><span style=\"font-size: 18px; font-family: 方正仿宋简体; color: black;\">第六十九条</span></strong></span>\r\n</p>\r\n<p style=\"text-indent:28px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">县级以上人民政府有关主管部门，未依照本条例的规定履行实验室及其实验活动监督检查职责的，由有关人民政府在各自职责范围内责令改正，通报批评；造成传染病传播、流行或者其他严重后果的，对直接负责的主管人员，依法给予行政处分；构成犯罪的，依法追究刑事责任。</span>\r\n</p>\r\n<p style=\"text-indent:48px\">\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><strong><span style=\"font-size: 24px; font-family: 方正仿宋简体; color: black;\">第七章</span></strong><strong> </strong><strong><span style=\"font-size: 24px; font-family: 方正仿宋简体; color: black;\">附</span></strong><strong> </strong><strong><span style=\"font-size: 24px; font-family: 方正仿宋简体; color: black;\">则</span></strong></span>\r\n</p>\r\n<p style=\"text-indent:36px\">\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><strong><span style=\"font-size: 18px; font-family: 方正仿宋简体; color: black;\">第七十条</span></strong></span>\r\n</p>\r\n<p style=\"text-indent:28px\">\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><span style=\"font-size: 14px; font-family: 方正仿宋简体; color: black;\">军队实验室由</span><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px; color: black;\"><a href=\"http://baike.baidu.com/view/24050.htm\" target=\"_blank\" style=\"outline: none\"><span style=\"font-size: 16px; font-family: 方正仿宋简体; color: black;\">中国人民解放军</span></a></span></span><span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">卫生主管部门参照本条例负责监督管理。</span>\r\n</p>\r\n<p style=\"text-indent:36px\">\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><strong><span style=\"font-size: 18px; font-family: 方正仿宋简体; color: black;\">第七十一条</span></strong></span>\r\n</p>\r\n<p style=\"text-indent:28px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">本条例施行前设立的实验室，应当自本条例施行之日起６个月内，依照本条例的规定，办理有关手续。</span>\r\n</p>\r\n<p style=\"text-indent:36px\">\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><strong><span style=\"font-size: 18px; font-family: 方正仿宋简体; color: black;\">第七十二条</span></strong></span>\r\n</p>\r\n<p style=\"text-indent:28px\">\r\n    <span style=\"color: black; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">本条例自公布之日起施行。</span>\r\n</p>\r\n<p style=\"text-indent:43px\">\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">&nbsp;</span>\r\n</p>\r\n<p>\r\n    <br/>\r\n</p>');
INSERT INTO `regulation_rules` VALUES ('4', '中华人民共和国放射性同位素与射线装置安全和防护条例', '2018-10-15', '<p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: 宋体, 微软雅黑; font-size: 14px; white-space: normal;\">\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">目录</span>\r\n</p>\r\n<p>\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-size: 16px;\">　　第一章总则</span>\r\n</p>\r\n<p>\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-size: 16px;\">　　第二章许可和备案</span>\r\n</p>\r\n<p>\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-size: 16px;\">　　第三章安全和防护</span>\r\n</p>\r\n<p>\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-size: 16px;\">　　第四章辐射事故应急处理</span>\r\n</p>\r\n<p>\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-size: 16px;\">　　第五章监督检查</span>\r\n</p>\r\n<p>\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-size: 16px;\">　　第六章法律责任</span>\r\n</p>\r\n<p>\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-size: 16px;\">　　第七章附则</span>\r\n</p>\r\n<p>\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><strong><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-size: 18pt;\">第一章总则</span></strong></span>\r\n</p>\r\n<p>\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-size: 16px;\">　　第一条为了加强对放射性同位素、射线装置安全和防护的监督管理，促进放射性同位素、射线装置的安全应用，保障人体健康，保护环境，制定本条例。</span>\r\n</p>\r\n<p>\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-size: 16px;\">　　第二条在中华人民共和国境内生产、销售、使用放射性同位素和射线装置，以及转让、进出口放射性同位素的，应当遵守本条例。</span>\r\n</p>\r\n<p>\r\n    <span style=\"margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　本条例所称放射性同位素包括放射源和非密封放射性物质。</span>\r\n</p>\r\n<p>\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-size: 16px;\">　　第三条国务院环境保护主管部门对全国放射性同位素、射线装置的安全和防护工作实施统一监督管理。</span>\r\n</p>\r\n<p>\r\n    <span style=\"margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　国务院公安、卫生等部门按照职责分工和本条例的规定，对有关放射性同位素、射线装置的安全和防护工作实施监督管理。</span>\r\n</p>\r\n<p>\r\n    <span style=\"margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　县级以上地方人民政府环境保护主管部门和其他有关部门，按照职责分工和本条例的规定，对本行政区域内放射性同位素、射线装置的安全和防护工作实施监督管理。</span>\r\n</p>\r\n<p>\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-size: 16px;\">　　第四条国家对放射源和射线装置实行分类管理。根据放射源、射线装置对人体健康和环境的潜在危害程度，从高到低将放射源分为Ⅰ类、Ⅱ类、Ⅲ类、Ⅳ类、Ⅴ类，具体分类办法由国务院环境保护主管部门制定；将射线装置分为Ⅰ类、Ⅱ类、Ⅲ类，具体分类办法由国务院环境保护主管部门商国务院卫生主管部门制定。</span>\r\n</p>\r\n<p>\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><strong><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-size: 18pt;\">第二章许可和备案</span></strong></span>\r\n</p>\r\n<p>\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-size: 16px;\">　　第五条生产、销售、使用放射性同位素和射线装置的单位，应当依照本章规定取得许可证。</span>\r\n</p>\r\n<p>\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-size: 16px;\">　　第六条生产放射性同位素、销售和使用Ⅰ类放射源、销售和使用Ⅰ类射线装置的单位的许可证，由国务院环境保护主管部门审批颁发。</span>\r\n</p>\r\n<p>\r\n    <span style=\"margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　前款规定之外的单位的许可证，由省、自治区、直辖市人民政府环境保护主管部门审批颁发。</span>\r\n</p>\r\n<p>\r\n    <span style=\"margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　国务院环境保护主管部门向生产放射性同位素的单位颁发许可证前，应当将申请材料印送其行业主管部门征求意见。</span>\r\n</p>\r\n<p>\r\n    <span style=\"margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　环境保护主管部门应当将审批颁发许可证的情况通报同级公安部门、卫生主管部门。</span>\r\n</p>\r\n<p>\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-size: 16px;\">　　第七条生产、销售、使用放射性同位素和射线装置的单位申请领取许可证，应当具备下列条件：</span>\r\n</p>\r\n<p>\r\n    <span style=\"margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　（一）有与所从事的生产、销售、使用活动规模相适应的，具备相应专业知识和防护知识及健康条件的专业技术人员；</span>\r\n</p>\r\n<p>\r\n    <span style=\"margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　（二）有符合国家环境保护标准、职业卫生标准和安全防护要求的场所、设施和设备；</span>\r\n</p>\r\n<p>\r\n    <span style=\"margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　（三）有专门的安全和防护管理机构或者专职、兼职安全和防护管理人员，并配备必要的防护用品和监测仪器；</span>\r\n</p>\r\n<p>\r\n    <span style=\"margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　（四）有健全的安全和防护管理规章制度、辐射事故应急措施；</span>\r\n</p>\r\n<p>\r\n    <span style=\"margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　（五）产生放射性废气、废液、固体废物的，具有确保放射性废气、废液、固体废物达标排放的处理能力或者可行的处理方案。</span>\r\n</p>\r\n<p>\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-size: 16px;\">　　第八条生产、销售、使用放射性同位素和射线装置的单位，应当事先向有审批权的环境保护主管部门提出许可申请，并提交符合本条例第七条规定条件的证明材料。</span>\r\n</p>\r\n<p>\r\n    <span style=\"margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　使用放射性同位素和射线装置进行放射诊疗的医疗卫生机构，还应当获得放射源诊疗技术和医用辐射机构许可。</span>\r\n</p>\r\n<p>\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-size: 16px;\">　　第九条环境保护主管部门应当自受理申请之日起20个工作日内完成审查，符合条件的，颁发许可证，并予以公告；不符合条件的，书面通知申请单位并说明理由。</span>\r\n</p>\r\n<p>\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-size: 16px;\">　　第十条许可证包括下列主要内容：</span>\r\n</p>\r\n<p>\r\n    <span style=\"margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　（一）单位的名称、地址、法定代表人；</span>\r\n</p>\r\n<p>\r\n    <span style=\"margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　（二）所从事活动的种类和范围；</span>\r\n</p>\r\n<p>\r\n    <span style=\"margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　（三）有效期限；</span>\r\n</p>\r\n<p>\r\n    <span style=\"margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　（四）发证日期和证书编号。</span>\r\n</p>\r\n<p>\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-size: 16px;\">　　第十一条持证单位变更单位名称、地址、法定代表人的，应当自变更登记之日起20日内，向原发证机关申请办理许可证变更手续。</span>\r\n</p>\r\n<p>\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-size: 16px;\">　　第十二条有下列情形之一的，持证单位应当按照原申请程序，重新申请领取许可证：</span>\r\n</p>\r\n<p>\r\n    <span style=\"margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　（一）改变所从事活动的种类或者范围的；</span>\r\n</p>\r\n<p>\r\n    <span style=\"margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　（二）新建或者改建、扩建生产、销售、使用设施或者场所的。</span>\r\n</p>\r\n<p>\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-size: 16px;\">　　第十三条许可证有效期为5年。有效期届满，需要延续的，持证单位应当于许可证有效期届满30日前，向原发证机关提出延续申请。原发证机关应当自受理延续申请之日起，在许可证有效期届满前完成审查，符合条件的，予以延续；不符合条件的，书面通知申请单位并说明理由。</span>\r\n</p>\r\n<p>\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-size: 16px;\">　　第十四条持证单位部分终止或者全部终止生产、销售、使用放射性同位素和射线装置活动的，应当向原发证机关提出部分变更或者注销许可证申请，由原发证机关核查合格后，予以变更或者注销许可证。</span>\r\n</p>\r\n<p>\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-size: 16px;\">　　第十五条禁止无许可证或者不按照许可证规定的种类和范围从事放射性同位素和射线装置的生产、销售、使用活动。</span>\r\n</p>\r\n<p>\r\n    <span style=\"margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　禁止伪造、变造、转让许可证。</span>\r\n</p>\r\n<p>\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-size: 16px;\">　　第十六条国务院对外贸易主管部门会同国务院环境保护主管部门、海关总署、国务院质量监督检验检疫部门和生产放射性同位素的单位的行业主管部门制定并公布限制进出口放射性同位素目录和禁止进出口放射性同位素目录。</span>\r\n</p>\r\n<p>\r\n    <span style=\"margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　进口列入限制进出口目录的放射性同位素，应当在国务院环境保护主管部门审查批准后，由国务院对外贸易主管部门依据国家对外贸易的有关规定签发进口许可证。进口限制进出口目录和禁止进出口目录之外的放射性同位素，依据国家对外贸易的有关规定办理进口手续。</span>\r\n</p>\r\n<p>\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-size: 16px;\">　　第十七条申请进口列入限制进出口目录的放射性同位素，应当符合下列要求：</span>\r\n</p>\r\n<p>\r\n    <span style=\"margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　（一）进口单位已经取得与所从事活动相符的许可证；</span>\r\n</p>\r\n<p>\r\n    <span style=\"margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　（二）进口单位具有进口放射性同位素使用期满后的处理方案，其中，进口Ⅰ类、Ⅱ类、Ⅲ类放射源的，应当具有原出口方负责回收的承诺文件；</span>\r\n</p>\r\n<p>\r\n    <span style=\"margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　（三）进口的放射源应当有明确标号和必要说明文件，其中，Ⅰ类、Ⅱ类、Ⅲ类放射源的标号应当刻制在放射源本体或者密封包壳体上，Ⅳ类、Ⅴ类放射源的标号应当记录在相应说明文件中；</span>\r\n</p>\r\n<p>\r\n    <span style=\"margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　（四）将进口的放射性同位素销售给其他单位使用的，还应当具有与使用单位签订的书面协议以及使用单位取得的许可证复印件。</span>\r\n</p>\r\n<p>\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-size: 16px;\">　　第十八条进口列入限制进出口目录的放射性同位素的单位，应当向国务院环境保护主管部门提出进口申请，并提交符合本条例第十七条规定要求的证明材料。</span>\r\n</p>\r\n<p>\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-size: 16px;\">　　国务院环境保护主管部门应当自受理申请之日起10个工作日内完成审查，符合条件的，予以批准；不符合条件的，书面通知申请单位并说明理由。</span>\r\n</p>\r\n<p>\r\n    <span style=\"margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　海关验凭放射性同位素进口许可证办理有关进口手续。进口放射性同位素的包装材料依法需要实施检疫的，依照国家有关检疫法律、法规的规定执行。</span>\r\n</p>\r\n<p>\r\n    <span style=\"margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　对进口的放射源，国务院环境保护主管部门还应当同时确定与其标号相对应的放射源编码。</span>\r\n</p>\r\n<p>\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-size: 16px;\">　　第十九条申请转让放射性同位素，应当符合下列要求：</span>\r\n</p>\r\n<p>\r\n    <span style=\"margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　（一）转出、转入单位持有与所从事活动相符的许可证；</span>\r\n</p>\r\n<p>\r\n    <span style=\"margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　（二）转入单位具有放射性同位素使用期满后的处理方案；</span>\r\n</p>\r\n<p>\r\n    <span style=\"margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　（三）转让双方已经签订书面转让协议。</span>\r\n</p>\r\n<p>\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-size: 16px;\">　　第二十条转让放射性同位素，由转入单位向其所在地省、自治区、直辖市人民政府环境保护主管部门提出申请，并提交符合本条例第十九条规定要求的证明材料。</span>\r\n</p>\r\n<p>\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-size: 16px;\">　　省、自治区、直辖市人民政府环境保护主管部门应当自受理申请之日起15个工作日内完成审查，符合条件的，予以批准；不符合条件的，书面通知申请单位并说明理由。</span>\r\n</p>\r\n<p>\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-size: 16px;\">　　第二十一条放射性同位素的转出、转入单位应当在转让活动完成之日起20日内，分别向其所在地省、自治区、直辖市人民政府环境保护主管部门备案。</span>\r\n</p>\r\n<p>\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-size: 16px;\">　　第二十二条生产放射性同位素的单位，应当建立放射性同位素产品台账，并按照国务院环境保护主管部门制定的编码规则，对生产的放射源统一编码。放射性同位素产品台账和放射源编码清单应当报国务院环境保护主管部门备案。</span>\r\n</p>\r\n<p>\r\n    <span style=\"margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　生产的放射源应当有明确标号和必要说明文件。其中，Ⅰ类、Ⅱ类、Ⅲ类放射源的标号应当刻制在放射源本体或者密封包壳体上，Ⅳ类、Ⅴ类放射源的标号应当记录在相应说明文件中。</span>\r\n</p>\r\n<p>\r\n    <span style=\"margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　国务院环境保护主管部门负责建立放射性同位素备案信息管理系统，与有关部门实行信息共享。</span>\r\n</p>\r\n<p>\r\n    <span style=\"margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　未列入产品台账的放射性同位素和未编码的放射源，不得出厂和销售。</span>\r\n</p>\r\n<p>\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-size: 16px;\">　　第二十三条持有放射源的单位将废旧放射源交回生产单位、返回原出口方或者送交放射性废物集中贮存单位贮存的，应当在该活动完成之日起20日内向其所在地省、自治区、直辖市人民政府环境保护主管部门备案。</span>\r\n</p>\r\n<p>\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-size: 16px;\">　　第二十四条本条例施行前生产和进口的放射性同位素，由放射性同位素持有单位在本条例施行之日起6个月内，到其所在地省、自治区、直辖市人民政府环境保护主管部门办理备案手续，省、自治区、直辖市人民政府环境保护主管部门应当对放射源进行统一编码。</span>\r\n</p>\r\n<p>\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-size: 16px;\">　　第二十五条使用放射性同位素的单位需要将放射性同位素转移到外省、自治区、直辖市使用的，应当持许可证复印件向使用地省、自治区、直辖市人民政府环境保护主管部门备案，并接受当地环境保护主管部门的监督管理。</span>\r\n</p>\r\n<p>\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-size: 16px;\">　　第二十六条出口列入限制进出口目录的放射性同位素，应当提供进口方可以合法持有放射性同位素的证明材料，并由国务院环境保护主管部门依照有关法律和我国缔结或者参加的国际条约、协定的规定，办理有关手续。</span>\r\n</p>\r\n<p>\r\n    <span style=\"margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　出口放射性同位素应当遵守国家对外贸易的有关规定。</span>\r\n</p>\r\n<p>\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><strong><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-size: 18pt;\">第三章安全和防护</span></strong></span>\r\n</p>\r\n<p>\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-size: 16px;\">　　第二十七条生产、销售、使用放射性同位素和射线装置的单位，应当对本单位的放射性同位素、射线装置的安全和防护工作负责，并依法对其造成的放射性危害承担责任。</span>\r\n</p>\r\n<p>\r\n    <span style=\"margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　生产放射性同位素的单位的行业主管部门，应当加强对生产单位安全和防护工作的管理，并定期对其执行法律、法规和国家标准的情况进行监督检查。</span>\r\n</p>\r\n<p>\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-size: 16px;\">　　第二十八条生产、销售、使用放射性同位素和射线装置的单位，应当对直接从事生产、销售、使用活动的工作人员进行安全和防护知识教育培训，并进行考核；考核不合格的，不得上岗。</span>\r\n</p>\r\n<p>\r\n    <span style=\"margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　辐射安全关键岗位应当由注册核安全工程师担任。辐射安全关键岗位名录由国务院环境保护主管部门商国务院有关部门制定并公布。</span>\r\n</p>\r\n<p>\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-size: 16px;\">　　第二十九条生产、销售、使用放射性同位素和射线装置的单位，应当严格按照国家关于个人剂量监测和健康管理的规定，对直接从事生产、销售、使用活动的工作人员进行个人剂量监测和职业健康检查，建立个人剂量档案和职业健康监护档案。</span>\r\n</p>\r\n<p>\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-size: 16px;\">　　第三十条生产、销售、使用放射性同位素和射线装置的单位，应当对本单位的放射性同位素、射线装置的安全和防护状况进行年度评估。发现安全隐患的，应当立即进行整改。</span>\r\n</p>\r\n<p>\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-size: 16px;\">　　第三十一条生产、销售、使用放射性同位素和射线装置的单位需要终止的，应当事先对本单位的放射性同位素和放射性废物进行清理登记，作出妥善处理，不得留有安全隐患。生产、销售、使用放射性同位素和射线装置的单位发生变更的，由变更后的单位承担处理责任。变更前当事人对此另有约定的，从其约定；但是，约定中不得免除当事人的处理义务。</span>\r\n</p>\r\n<p>\r\n    <span style=\"margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　在本条例施行前已经终止的生产、销售、使用放射性同位素和射线装置的单位，其未安全处理的废旧放射源和放射性废物，由所在地省、自治区、直辖市人民政府环境保护主管部门提出处理方案，及时进行处理。所需经费由省级以上人民政府承担。</span>\r\n</p>\r\n<p>\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-size: 16px;\">　　第三十二条生产、进口放射源的单位销售Ⅰ类、Ⅱ类、Ⅲ类放射源给其他单位使用的，应当与使用放射源的单位签订废旧放射源返回协议；使用放射源的单位应当按照废旧放射源返回协议规定将废旧放射源交回生产单位或者返回原出口方。确实无法交回生产单位或者返回原出口方的，送交有相应资质的放射性废物集中贮存单位贮存。</span>\r\n</p>\r\n<p>\r\n    <span style=\"margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　使用放射源的单位应当按照国务院环境保护主管部门的规定，将Ⅳ类、Ⅴ类废旧放射源进行包装整备后送交有相应资质的放射性废物集中贮存单位贮存。</span>\r\n</p>\r\n<p>\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-size: 16px;\">　　第三十三条使用Ⅰ类、Ⅱ类、Ⅲ类放射源的场所和生产放射性同位素的场所，以及终结运行后产生放射性污染的射线装置，应当依法实施退役。</span>\r\n</p>\r\n<p>\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-size: 16px;\">　　第三十四条生产、销售、使用、贮存放射性同位素和射线装置的场所，应当按照国家有关规定设置明显的放射性标志，其入口处应当按照国家有关安全和防护标准的要求，设置安全和防护设施以及必要的防护安全联锁、报警装置或者工作信号。射线装置的生产调试和使用场所，应当具有防止误操作、防止工作人员和公众受到意外照射的安全措施。</span>\r\n</p>\r\n<p>\r\n    <span style=\"margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　放射性同位素的包装容器、含放射性同位素的设备和射线装置，应当设置明显的放射性标识和中文警示说明；放射源上能够设置放射性标识的，应当一并设置。运输放射性同位素和含放射源的射线装置的工具，应当按照国家有关规定设置明显的放射性标志或者显示危险信号。</span>\r\n</p>\r\n<p>\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-size: 16px;\">　　第三十五条放射性同位素应当单独存放，不得与易燃、易爆、腐蚀性物品等一起存放，并指定专人负责保管。贮存、领取、使用、归还放射性同位素时，应当进行登记、检查，做到账物相符。对放射性同位素贮存场所应当采取防火、防水、防盗、防丢失、防破坏、防射线泄漏的安全措施。</span>\r\n</p>\r\n<p>\r\n    <span style=\"margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　对放射源还应当根据其潜在危害的大小，建立相应的多层防护和安全措施，并对可移动的放射源定期进行盘存，确保其处于指定位置，具有可靠的安全保障。</span>\r\n</p>\r\n<p>\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-size: 16px;\">　　第三十六条在室外、野外使用放射性同位素和射线装置的，应当按照国家安全和防护标准的要求划出安全防护区域，设置明显的放射性标志，必要时设专人警戒。</span>\r\n</p>\r\n<p>\r\n    <span style=\"margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　在野外进行放射性同位素示踪试验的，应当经省级以上人民政府环境保护主管部门商同级有关部门批准方可进行。</span>\r\n</p>\r\n<p>\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-size: 16px;\">　　第三十七条辐射防护器材、含放射性同位素的设备和射线装置，以及含有放射性物质的产品和伴有产生Ｘ射线的电器产品，应当符合辐射防护要求。不合格的产品不得出厂和销售。</span>\r\n</p>\r\n<p>\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-size: 16px;\">　　第三十八条使用放射性同位素和射线装置进行放射诊疗的医疗卫生机构，应当依据国务院卫生主管部门有关规定和国家标准，制定与本单位从事的诊疗项目相适应的质量保证方案，遵守质量保证监测规范，按照医疗照射正当化和辐射防护最优化的原则，避免一切不必要的照射，并事先告知患者和受检者辐射对健康的潜在影响。</span>\r\n</p>\r\n<p>\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-size: 16px;\">　　第三十九条金属冶炼厂回收冶炼废旧金属时，应当采取必要的监测措施，防止放射性物质熔入产品中。监测中发现问题的，应当及时通知所在地设区的市级以上人民政府环境保护主管部门。</span>\r\n</p>\r\n<p>\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><strong><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-size: 18pt;\">第四章辐射事故应急处理</span></strong></span>\r\n</p>\r\n<p>\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-size: 16px;\">　　第四十条根据辐射事故的性质、严重程度、可控性和影响范围等因素，从重到轻将辐射事故分为特别重大辐射事故、重大辐射事故、较大辐射事故和一般辐射事故四个等级。</span>\r\n</p>\r\n<p>\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-size: 16px;\">　　特别重大辐射事故，是指Ⅰ类、Ⅱ类放射源丢失、被盗、失控造成大范围严重辐射污染后果，或者放射性同位素和射线装置失控导致3人以上（含3人）急性死亡。</span>\r\n</p>\r\n<p>\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-size: 16px;\">　　重大辐射事故，是指Ⅰ类、Ⅱ类放射源丢失、被盗、失控，或者放射性同位素和射线装置失控导致2人以下（含2人）急性死亡或者10人以上（含10人）急性重度放射病、局部器官残疾。</span>\r\n</p>\r\n<p>\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-size: 16px;\">　　较大辐射事故，是指Ⅲ类放射源丢失、被盗、失控，或者放射性同位素和射线装置失控导致9人以下（含9人）急性重度放射病、局部器官残疾。</span>\r\n</p>\r\n<p>\r\n    <span style=\"margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　一般辐射事故，是指Ⅳ类、Ⅴ类放射源丢失、被盗、失控，或者放射性同位素和射线装置失控导致人员受到超过年剂量限值的照射。</span>\r\n</p>\r\n<p>\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-size: 16px;\">　　第四十一条县级以上人民政府环境保护主管部门应当会同同级公安、卫生、财政等部门编制辐射事故应急预案，报本级人民政府批准。辐射事故应急预案应当包括下列内容：</span>\r\n</p>\r\n<p>\r\n    <span style=\"margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　（一）应急机构和职责分工；</span>\r\n</p>\r\n<p>\r\n    <span style=\"margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　（二）应急人员的组织、培训以及应急和救助的装备、资金、物资准备；</span>\r\n</p>\r\n<p>\r\n    <span style=\"margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　（三）辐射事故分级与应急响应措施；</span>\r\n</p>\r\n<p>\r\n    <span style=\"margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　（四）辐射事故调查、报告和处理程序。</span>\r\n</p>\r\n<p>\r\n    <span style=\"margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　生产、销售、使用放射性同位素和射线装置的单位，应当根据可能发生的辐射事故的风险，制定本单位的应急方案，做好应急准备。</span>\r\n</p>\r\n<p>\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-size: 16px;\">　　第四十二条发生辐射事故时，生产、销售、使用放射性同位素和射线装置的单位应当立即启动本单位的应急方案，采取应急措施，并立即向当地环境保护主管部门、公安部门、卫生主管部门报告。</span>\r\n</p>\r\n<p>\r\n    <span style=\"margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　环境保护主管部门、公安部门、卫生主管部门接到辐射事故报告后，应当立即派人赶赴现场，进行现场调查，采取有效措施，控制并消除事故影响，同时将辐射事故信息报告本级人民政府和上级人民政府环境保护主管部门、公安部门、卫生主管部门。</span>\r\n</p>\r\n<p>\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-size: 16px;\">　　县级以上地方人民政府及其有关部门接到辐射事故报告后，应当按照事故分级报告的规定及时将辐射事故信息报告上级人民政府及其有关部门。发生特别重大辐射事故和重大辐射事故后，事故发生地省、自治区、直辖市人民政府和国务院有关部门应当在4小时内报告国务院；特殊情况下，事故发生地人民政府及其有关部门可以直接向国务院报告，并同时报告上级人民政府及其有关部门。</span>\r\n</p>\r\n<p>\r\n    <span style=\"margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　禁止缓报、瞒报、谎报或者漏报辐射事故。</span>\r\n</p>\r\n<p>\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-size: 16px;\">　　第四十三条在发生辐射事故或者有证据证明辐射事故可能发生时，县级以上人民政府环境保护主管部门有权采取下列临时控制措施：</span>\r\n</p>\r\n<p>\r\n    <span style=\"margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　（一）责令停止导致或者可能导致辐射事故的作业；</span>\r\n</p>\r\n<p>\r\n    <span style=\"margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　（二）组织控制事故现场。</span>\r\n</p>\r\n<p>\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-size: 16px;\">　　第四十四条辐射事故发生后，有关县级以上人民政府应当按照辐射事故的等级，启动并组织实施相应的应急预案。</span>\r\n</p>\r\n<p>\r\n    <span style=\"margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　县级以上人民政府环境保护主管部门、公安部门、卫生主管部门，按照职责分工做好相应的辐射事故应急工作：</span>\r\n</p>\r\n<p>\r\n    <span style=\"margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　（一）环境保护主管部门负责辐射事故的应急响应、调查处理和定性定级工作，协助公安部门监控追缴丢失、被盗的放射源；</span>\r\n</p>\r\n<p>\r\n    <span style=\"margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　（二）公安部门负责丢失、被盗放射源的立案侦查和追缴；</span>\r\n</p>\r\n<p>\r\n    <span style=\"margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　（三）卫生主管部门负责辐射事故的医疗应急。</span>\r\n</p>\r\n<p>\r\n    <span style=\"margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　环境保护主管部门、公安部门、卫生主管部门应当及时相互通报辐射事故应急响应、调查处理、定性定级、立案侦查和医疗应急情况。国务院指定的部门根据环境保护主管部门确定的辐射事故的性质和级别，负责有关国际信息通报工作。</span>\r\n</p>\r\n<p>\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-size: 16px;\">　　第四十五条发生辐射事故的单位应当立即将可能受到辐射伤害的人员送至当地卫生主管部门指定的医院或者有条件救治辐射损伤病人的医院，进行检查和治疗，或者请求医院立即派人赶赴事故现场，采取救治措施。</span>\r\n</p>\r\n<p>\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><strong><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-size: 18pt;\">第五章监督检查</span></strong></span>\r\n</p>\r\n<p>\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-size: 16px;\">　　第四十六条县级以上人民政府环境保护主管部门和其他有关部门应当按照各自职责对生产、销售、使用放射性同位素和射线装置的单位进行监督检查。</span>\r\n</p>\r\n<p>\r\n    <span style=\"margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　被检查单位应当予以配合，如实反映情况，提供必要的资料，不得拒绝和阻碍。</span>\r\n</p>\r\n<p>\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-size: 16px;\">　　第四十七条县级以上人民政府环境保护主管部门应当配备辐射防护安全监督员。辐射防护安全监督员由从事辐射防护工作，具有辐射防护安全知识并经省级以上人民政府环境保护主管部门认可的专业人员担任。辐射防护安全监督员应当定期接受专业知识培训和考核。</span>\r\n</p>\r\n<p>\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-size: 16px;\">　　第四十八条县级以上人民政府环境保护主管部门在监督检查中发现生产、销售、使用放射性同位素和射线装置的单位有不符合原发证条件的情形的，应当责令其限期整改。</span>\r\n</p>\r\n<p>\r\n    <span style=\"margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　监督检查人员依法进行监督检查时，应当出示证件，并为被检查单位保守技术秘密和业务秘密。</span>\r\n</p>\r\n<p>\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-size: 16px;\">　　第四十九条任何单位和个人对违反本条例的行为，有权向环境保护主管部门和其他有关部门检举；对环境保护主管部门和其他有关部门未依法履行监督管理职责的行为，有权向本级人民政府、上级人民政府有关部门检举。接到举报的有关人民政府、环境保护主管部门和其他有关部门对有关举报应当及时核实、处理。</span>\r\n</p>\r\n<p>\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><strong><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-size: 18pt;\">第六章法律责任</span></strong></span>\r\n</p>\r\n<p>\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-size: 16px;\">　　第五十条违反本条例规定，县级以上人民政府环境保护主管部门有下列行为之一的，对直接负责的主管人员和其他直接责任人员，依法给予行政处分；构成犯罪的，依法追究刑事责任：</span>\r\n</p>\r\n<p>\r\n    <span style=\"margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　（一）向不符合本条例规定条件的单位颁发许可证或者批准不符合本条例规定条件的单位进口、转让放射性同位素的；</span>\r\n</p>\r\n<p>\r\n    <span style=\"margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　（二）发现未依法取得许可证的单位擅自生产、销售、使用放射性同位素和射线装置，不予查处或者接到举报后不依法处理的；</span>\r\n</p>\r\n<p>\r\n    <span style=\"margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　（三）发现未经依法批准擅自进口、转让放射性同位素，不予查处或者接到举报后不依法处理的；</span>\r\n</p>\r\n<p>\r\n    <span style=\"margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　（四）对依法取得许可证的单位不履行监督管理职责或者发现违反本条例规定的行为不予查处的；</span>\r\n</p>\r\n<p>\r\n    <span style=\"margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　（五）在放射性同位素、射线装置安全和防护监督管理工作中有其他渎职行为的。</span>\r\n</p>\r\n<p>\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-size: 16px;\">　　第五十一条违反本条例规定，县级以上人民政府环境保护主管部门和其他有关部门有下列行为之一的，对直接负责的主管人员和其他直接责任人员，依法给予行政处分；构成犯罪的，依法追究刑事责任：</span>\r\n</p>\r\n<p>\r\n    <span style=\"margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　（一）缓报、瞒报、谎报或者漏报辐射事故的；</span>\r\n</p>\r\n<p>\r\n    <span style=\"margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　（二）未按照规定编制辐射事故应急预案或者不依法履行辐射事故应急职责的。</span>\r\n</p>\r\n<p>\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-size: 16px;\">　　第五十二条违反本条例规定，生产、销售、使用放射性同位素和射线装置的单位有下列行为之一的，由县级以上人民政府环境保护主管部门责令停止违法行为，限期改正；逾期不改正的，责令停产停业或者由原发证机关吊销许可证；有违法所得的，没收违法所得；违法所得10万元以上的，并处违法所得1倍以上5倍以下的罚款；没有违法所得或者违法所得不足10万元的，并处1万元以上10万元以下的罚款：</span>\r\n</p>\r\n<p>\r\n    <span style=\"margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　（一）无许可证从事放射性同位素和射线装置生产、销售、使用活动的；</span>\r\n</p>\r\n<p>\r\n    <span style=\"margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　（二）未按照许可证的规定从事放射性同位素和射线装置生产、销售、使用活动的；</span>\r\n</p>\r\n<p>\r\n    <span style=\"margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　（三）改变所从事活动的种类或者范围以及新建、改建或者扩建生产、销售、使用设施或者场所，未按照规定重新申请领取许可证的；</span>\r\n</p>\r\n<p>\r\n    <span style=\"margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　（四）许可证有效期届满，需要延续而未按照规定办理延续手续的；</span>\r\n</p>\r\n<p>\r\n    <span style=\"margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　（五）未经批准，擅自进口或者转让放射性同位素的。</span>\r\n</p>\r\n<p>\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-size: 16px;\">　　第五十三条违反本条例规定，生产、销售、使用放射性同位素和射线装置的单位变更单位名称、地址、法定代表人，未依法办理许可证变更手续的，由县级以上人民政府环境保护主管部门责令限期改正，给予警告；逾期不改正的，由原发证机关暂扣或者吊销许可证。</span>\r\n</p>\r\n<p>\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-size: 16px;\">　　第五十四条违反本条例规定，生产、销售、使用放射性同位素和射线装置的单位部分终止或者全部终止生产、销售、使用活动，未按照规定办理许可证变更或者注销手续的，由县级以上人民政府环境保护主管部门责令停止违法行为，限期改正；逾期不改正的，处1万元以上10万元以下的罚款；造成辐射事故，构成犯罪的，依法追究刑事责任。</span>\r\n</p>\r\n<p>\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-size: 16px;\">　　第五十五条违反本条例规定，伪造、变造、转让许可证的，由县级以上人民政府环境保护主管部门收缴伪造、变造的许可证或者由原发证机关吊销许可证，并处5万元以上10万元以下的罚款；构成犯罪的，依法追究刑事责任。</span>\r\n</p>\r\n<p>\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-size: 16px;\">　　违反本条例规定，伪造、变造、转让放射性同位素进口和转让批准文件的，由县级以上人民政府环境保护主管部门收缴伪造、变造的批准文件或者由原批准机关撤销批准文件，并处5万元以上10万元以下的罚款；情节严重的，可以由原发证机关吊销许可证；构成犯罪的，依法追究刑事责任。</span>\r\n</p>\r\n<p>\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-size: 16px;\">　　第五十六条违反本条例规定，生产、销售、使用放射性同位素的单位有下列行为之一的，由县级以上人民政府环境保护主管部门责令限期改正，给予警告；逾期不改正的，由原发证机关暂扣或者吊销许可证：</span>\r\n</p>\r\n<p>\r\n    <span style=\"margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　（一）转入、转出放射性同位素未按照规定备案的；</span>\r\n</p>\r\n<p>\r\n    <span style=\"margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　（二）将放射性同位素转移到外省、自治区、直辖市使用，未按照规定备案的；</span>\r\n</p>\r\n<p>\r\n    <span style=\"margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　（三）将废旧放射源交回生产单位、返回原出口方或者送交放射性废物集中贮存单位贮存，未按照规定备案的。</span>\r\n</p>\r\n<p>\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-size: 16px;\">　　第五十七条违反本条例规定，生产、销售、使用放射性同位素和射线装置的单位有下列行为之一的，由县级以上人民政府环境保护主管部门责令停止违法行为，限期改正；逾期不改正的，处1万元以上10万元以下的罚款：</span>\r\n</p>\r\n<p>\r\n    <span style=\"margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　（一）在室外、野外使用放射性同位素和射线装置，未按照国家有关安全和防护标准的要求划出安全防护区域和设置明显的放射性标志的；</span>\r\n</p>\r\n<p>\r\n    <span style=\"margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　（二）未经批准擅自在野外进行放射性同位素示踪试验的。</span>\r\n</p>\r\n<p>\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-size: 16px;\">　　第五十八条违反本条例规定，生产放射性同位素的单位有下列行为之一的，由县级以上人民政府环境保护主管部门责令限期改正，给予警告；逾期不改正的，依法收缴其未备案的放射性同位素和未编码的放射源，处5万元以上10万元以下的罚款，并可以由原发证机关暂扣或者吊销许可证：</span>\r\n</p>\r\n<p>\r\n    <span style=\"margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　（一）未建立放射性同位素产品台账的；</span>\r\n</p>\r\n<p>\r\n    <span style=\"margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　（二）未按照国务院环境保护主管部门制定的编码规则，对生产的放射源进行统一编码的；</span>\r\n</p>\r\n<p>\r\n    <span style=\"margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　（三）未将放射性同位素产品台账和放射源编码清单报国务院环境保护主管部门备案的；</span>\r\n</p>\r\n<p>\r\n    <span style=\"margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　（四）出厂或者销售未列入产品台账的放射性同位素和未编码的放射源的。</span>\r\n</p>\r\n<p>\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-size: 16px;\">　　第五十九条违反本条例规定，生产、销售、使用放射性同位素和射线装置的单位有下列行为之一的，由县级以上人民政府环境保护主管部门责令停止违法行为，限期改正；逾期不改正的，由原发证机关指定有处理能力的单位代为处理或者实施退役，费用由生产、销售、使用放射性同位素和射线装置的单位承担，并处1万元以上l0万元以下的罚款：</span>\r\n</p>\r\n<p>\r\n    <span style=\"margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　（一）未按照规定对废旧放射源进行处理的；</span>\r\n</p>\r\n<p>\r\n    <span style=\"margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　（二）未按照规定对使用Ⅰ类、Ⅱ类、Ⅲ类放射源的场所和生产放射性同位素的场所，以及终结运行后产生放射性污染的射线装置实施退役的。</span>\r\n</p>\r\n<p>\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-size: 16px;\">　　第六十条违反本条例规定，生产、销售、使用放射性同位素和射线装置的单位有下列行为之一的，由县级以上人民政府环境保护主管部门责令停止违法行为，限期改正；逾期不改正的，责令停产停业，并处2万元以上20万元以下的罚款；构成犯罪的，依法追究刑事责任：</span>\r\n</p>\r\n<p>\r\n    <span style=\"margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　（一）未按照规定对本单位的放射性同位素、射线装置安全和防护状况进行评估或者发现安全隐患不及时整改的；</span>\r\n</p>\r\n<p>\r\n    <span style=\"margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　（二）生产、销售、使用、贮存放射性同位素和射线装置的场所未按照规定设置安全和防护设施以及放射性标志的。</span>\r\n</p>\r\n<p>\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-size: 16px;\">　　第六十一条违反本条例规定，造成辐射事故的，由原发证机关责令限期改正，并处5万元以上20万元以下的罚款；情节严重的，由原发证机关吊销许可证；构成违反治安管理行为的，由公安机关依法予以治安处罚；构成犯罪的，依法追究刑事责任。</span>\r\n</p>\r\n<p>\r\n    <span style=\"margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　因辐射事故造成他人损害的，依法承担民事责任。</span>\r\n</p>\r\n<p>\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-size: 16px;\">　　第六十二条生产、销售、使用放射性同位素和射线装置的单位被责令限期整改，逾期不整改或者经整改仍不符合原发证条件的，由原发证机关暂扣或者吊销许可证。</span>\r\n</p>\r\n<p>\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-size: 16px;\">　　第六十三条违反本条例规定，被依法吊销许可证的单位或者伪造、变造许可证的单位，5年内不得申请领取许可证。</span>\r\n</p>\r\n<p>\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-size: 16px;\">　　第六十四条县级以上地方人民政府环境保护主管部门的行政处罚权限的划分，由省、自治区、直辖市人民政府确定。</span>\r\n</p>\r\n<p>\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><strong><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-size: 18pt;\">第七章附则</span></strong></span>\r\n</p>\r\n<p>\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-size: 16px;\">　　第六十五条军用放射性同位素、射线装置安全和防护的监督管理，依照《<a href=\"http://baike.baidu.com/view/248762.htm\" target=\"_blank\" style=\"text-decoration-line: none; outline: none; color: blue;\"><span style=\"letter-spacing: 0.4pt; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; margin: 0px; padding: 0px; color: rgb(51, 102, 204);\">中华人民共和国放射性污染防治法</span></a>》第六十条的规定执行。</span>\r\n</p>\r\n<p>\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-size: 16px;\">　　第六十六条劳动者在职业活动中接触放射性同位素和射线装置造成的职业病的防治，依照《中华人民共和国职业病防治法》和国务院有关规定执行。</span>\r\n</p>\r\n<p>\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-size: 16px;\">　　第六十七条放射性同位素的运输，放射性同位素和射线装置生产、销售、使用过程中产生的放射性废物的处置，依照国务院有关规定执行。</span>\r\n</p>\r\n<p>\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-size: 16px;\">　　第六十八条本条例中下列用语的含义：</span>\r\n</p>\r\n<p>\r\n    <span style=\"margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　放射性同位素，是指某种发生放射性衰变的元素中具有相同原子序数但质量不同的核素。</span>\r\n</p>\r\n<p>\r\n    <span style=\"margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　放射源，是指除研究堆和动力堆核燃料循环范畴的材料以外，永久密封在容器中或者有严密包层并呈固态的放射性材料。</span>\r\n</p>\r\n<p>\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-size: 16px;\">　　射线装置，是指X线机、加速器、中子发生器以及含放射源的装置。</span>\r\n</p>\r\n<p>\r\n    <span style=\"margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　非密封放射性物质，是指非永久密封在包壳里或者紧密地固结在覆盖层里的放射性物质。</span>\r\n</p>\r\n<p>\r\n    <span style=\"margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　转让，是指除进出口、回收活动之外，放射性同位素所有权或者使用权在不同持有者之间的转移。</span>\r\n</p>\r\n<p>\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-size: 16px;\">　　伴有产生X射线的电器产品，是指不以产生X射线为目的，但在生产或者使用过程中产生X射线的电器产品。</span>\r\n</p>\r\n<p>\r\n    <span style=\"margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">　　辐射事故，是指放射源丢失、被盗、失控，或者放射性同位素和射线装置失控导致人员受到意外的异常照射。</span>\r\n</p>\r\n<p>\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; margin: 0px; padding: 0px; letter-spacing: 0.4pt; font-size: 16px;\">　　第六十九条本条例自2005年12月1日起施行。1989年10月24日国务院发布的《<a href=\"http://baike.baidu.com/view/437709.htm\" target=\"_blank\" style=\"text-decoration-line: none; outline: none; color: blue;\"><span style=\"letter-spacing: 0.4pt; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; margin: 0px; padding: 0px; color: rgb(51, 102, 204);\">放射性同位素与射线装置放射防护条例</span></a>》同时废止。</span>\r\n</p>\r\n<p>\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><br/></span>\r\n</p>');
INSERT INTO `regulation_rules` VALUES ('5', '中华人民共和国计算机信息系统安全保护条例', '2018-10-11', '<p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: 宋体, 微软雅黑; font-size: 14px; white-space: normal; text-align: center;\">\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\"><span style=\"margin: 0px; padding: 0px; font-size: 12pt;\">1994年2月18日中华人民共和国国务院令第147号发布）</span>&nbsp;</span>\r\n</p>\r\n<p>\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">&nbsp;</span>\r\n</p>\r\n<p>\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\"><strong><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; margin: 0px; padding: 0px; font-size: 15pt;\">目　 录</span></strong></span>\r\n</p>\r\n<p>\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\"><strong><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; margin: 0px; padding: 0px; font-size: 12pt;\">　　第一章　总　则</span></strong></span>\r\n</p>\r\n<p>\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\"><strong><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; margin: 0px; padding: 0px; font-size: 12pt;\">　　第二章　安全保护制度</span></strong></span>\r\n</p>\r\n<p>\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\"><strong><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; margin: 0px; padding: 0px; font-size: 12pt;\">　　第三章　安全监督</span></strong></span>\r\n</p>\r\n<p>\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\"><strong><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; margin: 0px; padding: 0px; font-size: 12pt;\">　　第四章　法律责任</span></strong></span>\r\n</p>\r\n<p>\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\"><strong><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; margin: 0px; padding: 0px; font-size: 12pt;\">　　第五章　附　则</span></strong></span>\r\n</p>\r\n<p>\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\"><strong><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; margin: 0px; padding: 0px; font-size: 15pt;\">第一章　总&nbsp; 则</span></strong></span>\r\n</p>\r\n<p>\r\n    <span style=\"margin: 0px; padding: 0px; font-size: 12pt; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">　　<strong>第一条</strong>　为了保护计算机信息系统的安全，促进计算机的应用和发展，保障社会主义现代化建设的顺利进行，制定本条例。</span>\r\n</p>\r\n<p>\r\n    <span style=\"margin: 0px; padding: 0px; font-size: 12pt; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">　　<strong>第二条</strong>　本条例所称的计算机信息系统，是指由计算机及其相关的和配套的设备、设施（含网络）构成的，按照一定的应用目标和规则对信息进行采集、加工、存储、传输、检索等处理的人机系统。</span>\r\n</p>\r\n<p>\r\n    <span style=\"margin: 0px; padding: 0px; font-size: 12pt; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">　　<strong>第三条</strong>　计算机信息系统的安全保护，应当保障计算机及其相关的和配套的设备、设施（含网络）的安全，运行环境的安全，保障信息的安全，保障计算机功能的正常发挥，以维护计算机信息系统的安全运行。</span>\r\n</p>\r\n<p>\r\n    <span style=\"margin: 0px; padding: 0px; font-size: 12pt; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">　　<strong>第四条</strong>　计算机信息系统的安全保护工作，重点维护国家事务、经济建设、国防建设、尖端科学技术等重要领域的计算机信息系统的安全。</span>\r\n</p>\r\n<p>\r\n    <span style=\"margin: 0px; padding: 0px; font-size: 12pt; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">　　<strong>第五条</strong>　中华人民共和国境内的计算机信息系统的安全保护，适用本条例。</span>\r\n</p>\r\n<p>\r\n    <span style=\"margin: 0px; padding: 0px; font-size: 12pt; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">　　未联网的微型计算机的安全保护办法，另行制定。</span>\r\n</p>\r\n<p>\r\n    <span style=\"margin: 0px; padding: 0px; font-size: 12pt; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">　　<strong>第六条</strong>　公安部主管全国计算机信息系统安全保护工作。</span>\r\n</p>\r\n<p>\r\n    <span style=\"margin: 0px; padding: 0px; font-size: 12pt; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">　　国家安全部、国家保密局和国务院其他有关部门，在国务院规定的职责范围内做好计算机信息系统安全保护的有关工作。</span>\r\n</p>\r\n<p>\r\n    <span style=\"margin: 0px; padding: 0px; font-size: 12pt; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">　　<strong>第七条</strong>　任何组织或者个人，不得利用计算机信息系统从事危害国家利益、集体利益和公民合法利益的活动，不得危害计算机信息系统的安全。</span>\r\n</p>\r\n<p>\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\"><strong><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; margin: 0px; padding: 0px; font-size: 15pt;\">第二章　安全保护制度</span></strong></span>\r\n</p>\r\n<p>\r\n    <span style=\"margin: 0px; padding: 0px; font-size: 12pt; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">　　<strong>第八条</strong>　计算机信息系统的建设和应用，应当遵守法律、行政法规和国家其他有关规定。</span>\r\n</p>\r\n<p>\r\n    <span style=\"margin: 0px; padding: 0px; font-size: 12pt; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">　　<strong>第九条</strong>　计算机信息系统实行安全等级保护。安全等级的划分标准和安全等级保护的具体办法，由公安部会同有关部门制定。</span>\r\n</p>\r\n<p>\r\n    <span style=\"margin: 0px; padding: 0px; font-size: 12pt; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">　　<strong>第十条</strong>　计算机机房应当符合国家标准和国家有关规定。</span>\r\n</p>\r\n<p>\r\n    <span style=\"margin: 0px; padding: 0px; font-size: 12pt; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">　　在计算机机房附近施工，不得危害计算机信息系统的安全。</span>\r\n</p>\r\n<p>\r\n    <span style=\"margin: 0px; padding: 0px; font-size: 12pt; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">　　<strong>第十一条</strong>　进行国际联网的计算机信息系统，由计算机信息系统的使用单位报省级以上人民政府公安机关备案。</span>\r\n</p>\r\n<p>\r\n    <span style=\"margin: 0px; padding: 0px; font-size: 12pt; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">　　<strong>第十二条</strong>　运输、携带、邮寄计算机信息媒体进出境的，应当如实向海关申报。</span>\r\n</p>\r\n<p>\r\n    <span style=\"margin: 0px; padding: 0px; font-size: 12pt; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">　　<strong>第十三条</strong>　计算机信息系统的使用单位应当建立健全安全管理制度，负责本单位计算机信息系统的安全保护工作。</span>\r\n</p>\r\n<p>\r\n    <span style=\"margin: 0px; padding: 0px; font-size: 12pt; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">　　<strong>第十四条</strong>　对计算机信息系统中发生的案件，有关使用单位应当在２４小时内向当地县级以上人民政府公安机关报告。</span>\r\n</p>\r\n<p>\r\n    <span style=\"margin: 0px; padding: 0px; font-size: 12pt; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">　　<strong>第十五条</strong>　对计算机病毒和危害社会公共安全的其他有害数据的防治研究工作，由公安部归口管理。</span>\r\n</p>\r\n<p>\r\n    <span style=\"margin: 0px; padding: 0px; font-size: 12pt; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">　　<strong>第十六条</strong>　国家对计算机信息系统安全专用产品的销售实行许可证制度。具体办法由公安部会同有关部门制定。</span>\r\n</p>\r\n<p>\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\"><strong><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; margin: 0px; padding: 0px; font-size: 15pt;\">第三章　安全监督</span></strong></span>\r\n</p>\r\n<p>\r\n    <span style=\"margin: 0px; padding: 0px; font-size: 12pt; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">　　<strong>第十七条</strong>　公安机关对计算机信息系统安全保护工作行使下列监督职权：</span>\r\n</p>\r\n<p>\r\n    <span style=\"margin: 0px; padding: 0px; font-size: 12pt; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">　　（一）监督、检查、指导计算机信息系统安全保护工作；</span>\r\n</p>\r\n<p>\r\n    <span style=\"margin: 0px; padding: 0px; font-size: 12pt; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">　　（二）查处危害计算机信息系统安全的违法犯罪案件；</span>\r\n</p>\r\n<p>\r\n    <span style=\"margin: 0px; padding: 0px; font-size: 12pt; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">　　（三）履行计算机信息系统安全保护工作的其他监督职责。</span>\r\n</p>\r\n<p>\r\n    <span style=\"margin: 0px; padding: 0px; font-size: 12pt; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">　　<strong>第十八条</strong>　公安机关发现影响计算机信息系统安全的隐患时，应当及时通知使用单位采取安全保护措施。</span>\r\n</p>\r\n<p>\r\n    <span style=\"margin: 0px; padding: 0px; font-size: 12pt; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">　　<strong>第十九条</strong>　公安部在紧急情况下，可以就涉及计算机信息系统安全的特定事项发布专项通令。</span>\r\n</p>\r\n<p>\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\"><strong><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; margin: 0px; padding: 0px; font-size: 15pt;\">第四章　法律责任</span></strong></span>\r\n</p>\r\n<p>\r\n    <span style=\"margin: 0px; padding: 0px; font-size: 12pt; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">　　<strong>第二十条</strong>　违反本条例的规定，有下列行为之一的，由公安机关处以警告或者停机整顿：</span>\r\n</p>\r\n<p>\r\n    <span style=\"margin: 0px; padding: 0px; font-size: 12pt; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">　　（一）违反计算机信息系统安全等级保护制度，危害计算机信息系统安全的；</span>\r\n</p>\r\n<p>\r\n    <span style=\"margin: 0px; padding: 0px; font-size: 12pt; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">　　（二）违反计算机信息系统国际联网备案制度的；</span>\r\n</p>\r\n<p>\r\n    <span style=\"margin: 0px; padding: 0px; font-size: 12pt; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">　　（三）不按照规定时间报告计算机信息系统中发生的案件的；</span>\r\n</p>\r\n<p>\r\n    <span style=\"margin: 0px; padding: 0px; font-size: 12pt; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">　　（四）接到公安机关要求改进安全状况的通知后，在限期内拒不改进的；</span>\r\n</p>\r\n<p>\r\n    <span style=\"margin: 0px; padding: 0px; font-size: 12pt; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">　　（五）有危害计算机信息系统安全的其他行为的。</span>\r\n</p>\r\n<p>\r\n    <span style=\"margin: 0px; padding: 0px; font-size: 12pt; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">　　<strong>第二十一条</strong>　计算机机房不符合国家标准和国家其他有关规定的，或者在计算机机房附近施工危害计算机信息系统安全的，由公安机关会同有关单位进行处理。</span>\r\n</p>\r\n<p>\r\n    <span style=\"margin: 0px; padding: 0px; font-size: 12pt; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">　　<strong>第二十二条</strong>　运输、携带、邮寄计算机信息媒体进出境，不如实向海关申报的，由海关依照《中华人民共和国海关法》和本条例以及其他有关法律、法规的规定处理。　</span>\r\n</p>\r\n<p>\r\n    <span style=\"margin: 0px; padding: 0px; font-size: 12pt; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">　　<strong>第二十三条</strong>　故意输入计算机病毒以及其他有害数据危害计算机信息系统安全的，或者未经许可出售计算机信息系统安全专用产品的，由公安机关处以警告或者对个人处以５０００元以下的罚款、对单位处以１５０００元以下的罚款；有违法所得的，除予以没收外，可以处以违法所得１至３倍的罚款。</span>\r\n</p>\r\n<p>\r\n    <span style=\"margin: 0px; padding: 0px; font-size: 12pt; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">　　<strong>第二十四条</strong>　违反本条例的规定，构成违反治安管理行为的，依照《中华人民共和国治安管理处罚法》的有关规定处罚；构成犯罪的，依法追究刑事责任。</span>\r\n</p>\r\n<p>\r\n    <span style=\"margin: 0px; padding: 0px; font-size: 12pt; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">　　<strong>第二十五条</strong>　任何组织或者个人违反本条例的规定，给国家、集体或者他人财产造成损失的，应当依法承担民事责任。</span>\r\n</p>\r\n<p>\r\n    <span style=\"margin: 0px; padding: 0px; font-size: 12pt; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">　　<strong>第二十六条</strong>　当事人对公安机关依照本条例所作出的具体行政行为不服的，可以依法申请行政复议或者提起行政诉讼。</span>\r\n</p>\r\n<p>\r\n    <span style=\"margin: 0px; padding: 0px; font-size: 12pt; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">　　<strong>第二十七条</strong>　执行本条例的国家公务员利用职权，索取、收受贿赂或者有其他违法、失职行为，构成犯罪的，依法追究刑事责任；尚不构成犯罪的，给予行政处分。</span>\r\n</p>\r\n<p>\r\n    <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\"><strong><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; margin: 0px; padding: 0px; font-size: 15pt;\">第五章　附&nbsp;则</span></strong></span>\r\n</p>\r\n<p>\r\n    <span style=\"margin: 0px; padding: 0px; font-size: 12pt; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">　　<strong>第二十八条</strong>　本条例下列用语的含义：</span>\r\n</p>\r\n<p>\r\n    <span style=\"margin: 0px; padding: 0px; font-size: 12pt; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">　　计算机病毒，是指编制或者在计算机程序中插入的破坏计算机功能或者毁坏数据，影响计算机使用，并能自我复制的一组计算机指令或者程序代码。</span>\r\n</p>\r\n<p>\r\n    <span style=\"margin: 0px; padding: 0px; font-size: 12pt; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">　　计算机信息系统安全专用产品，是指用于保护计算机信息系统安全的专用硬件和软件产品。</span>\r\n</p>\r\n<p>\r\n    <span style=\"margin: 0px; padding: 0px; font-size: 12pt; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">　　<strong>第二十九条</strong>　军队的计算机信息系统安全保护工作，按照军队的有关法规执行。</span>\r\n</p>\r\n<p>\r\n    <span style=\"margin: 0px; padding: 0px; font-size: 12pt; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">　　<strong>第三十条</strong>　公安部可以根据本条例制定实施办法。</span>\r\n</p>\r\n<p>\r\n    <span style=\"margin: 0px; padding: 0px; font-size: 12pt; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">　　<strong>第三十一条</strong>　本条例自发布之日起施行。</span>\r\n</p>\r\n<p>\r\n    <br/>\r\n</p>');
